import React, { useEffect, useRef, useState } from "react";
import * as d3 from "d3";
import { toPng } from "html-to-image";
import "./GraphVisualization.css";
import db from "./assets/down-arrow_752650.png";

const GraphVisualization = () => {
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  const [input, setInput] = useState("");
  const [error, setError] = useState("");
  const [isDirected, setIsDirected] = useState(true);

  const svgRef = useRef();
  const graphContainerRef = useRef();

  const handleInputChange = (e) => {
    const value = e.target.value;
    setInput(value);
    validateInput(value);
  };

  const validateInput = (input) => {
    try {
      const lines = input.trim().split("\n");
      const newNodes = [];
      const newEdges = [];

      lines.forEach((line) => {
        const [from, to] = line.trim().split(" ");
        if (from === undefined || to === undefined || isNaN(from) || isNaN(to)) {
          throw new Error("Invalid input format! Each line must have two integers.");
        }

        if (!newNodes.some((node) => node.id === from)) newNodes.push({ id: from });
        if (!newNodes.some((node) => node.id === to)) newNodes.push({ id: to });

        newEdges.push({ source: from, target: to });

        if (!isDirected) {
          newEdges.push({ source: to, target: from });
        }
      });

      const uniqueEdges = newEdges.filter(
        (edge, index, self) =>
          index ===
          self.findIndex(
            (e) =>
              (e.source === edge.source && e.target === edge.target) ||
              (e.source === edge.target && e.target === edge.source)
          )
      );

      setNodes(newNodes);
      setEdges(uniqueEdges);
      setError("");
    } catch (error) {
      setError(error.message);
    }
  };

  const createAdjacencyList = (nodes, edges) => {
    const adjacencyList = new Map();
    nodes.forEach((node) => adjacencyList.set(node.id, []));
    edges.forEach((edge) => {
      adjacencyList.get(edge.source).push(edge.target);
      if (!isDirected) {
        adjacencyList.get(edge.target).push(edge.source);
      }
    });
    return adjacencyList;
  };

  const bfs = (startNode) => {
    const adjacencyList = createAdjacencyList(nodes, edges);
    const visited = new Set();
    const queue = [startNode];
    const result = [];

    while (queue.length > 0) {
      const node = queue.shift();
      if (!visited.has(node)) {
        visited.add(node);
        result.push(node);
        const neighbors = adjacencyList.get(node) || [];
        queue.push(...neighbors.filter((neighbor) => !visited.has(neighbor)));
      }
    }

    return result;
  };

  const dfs = (startNode) => {
    const adjacencyList = createAdjacencyList(nodes, edges);
    const visited = new Set();
    const result = [];

    const dfsRecursive = (node) => {
      if (!visited.has(node)) {
        visited.add(node);
        result.push(node);
        const neighbors = adjacencyList.get(node) || [];
        neighbors.forEach(dfsRecursive);
      }
    };

    dfsRecursive(startNode);
    return result;
  };

  const downloadGraph = () => {
    if (graphContainerRef.current) {
      toPng(graphContainerRef.current)
        .then((dataUrl) => {
          const link = document.createElement("a");
          link.download = "graph_visualization.png";
          link.href = dataUrl;
          link.click();
        })
        .catch((err) => {
          console.error("Failed to download graph as PNG:", err);
        });
    }
  };

  useEffect(() => {
    const svg = d3.select(svgRef.current);
    svg.selectAll("*").remove();

    if (nodes.length === 0 || edges.length === 0) return;

    const width = 700;
    const height = 350;

    const simulation = d3
      .forceSimulation(nodes)
      .force("link", d3.forceLink(edges).id((d) => d.id).distance(100))
      .force("charge", d3.forceManyBody().strength(-300))
      .force("center", d3.forceCenter(width / 2, height / 2))
      .force("x", d3.forceX(width / 2).strength(0.1))
      .force("y", d3.forceY(height / 2).strength(0.1))
      .on("tick", ticked);

    function constrainBounds(d) {
      d.x = Math.max(20, Math.min(width - 20, d.x));
      d.y = Math.max(20, Math.min(height - 20, d.y));
    }

    svg
      .append("defs")
      .append("marker")
      .attr("id", "arrowhead")
      .attr("viewBox", "0 -5 10 10")
      .attr("refX", 25)
      .attr("refY", 0)
      .attr("orient", "auto")
      .attr("markerWidth", 6)
      .attr("markerHeight", 6)
      .append("path")
      .attr("d", "M0,-5L10,0L0,5")
      .attr("fill", "#42a5f5");

    const link = svg
      .append("g")
      .attr("class", "links")
      .selectAll("line")
      .data(edges)
      .enter()
      .append("line")
      .attr("stroke", "#333")
      .attr("stroke-width", 2)
      .attr("marker-end", isDirected ? "url(#arrowhead)" : null);

    const node = svg
      .append("g")
      .attr("class", "nodes")
      .selectAll("circle")
      .data(nodes)
      .enter()
      .append("circle")
      .attr("r", 20)
      .attr("fill", "#4CAF50")
      .attr("stroke", "black")
      .attr("stroke-width", 2)
      .call(
        d3
          .drag()
          .on("start", dragStarted)
          .on("drag", dragged)
          .on("end", dragEnded)
      );

    const label = svg
      .append("g")
      .attr("class", "labels")
      .selectAll("text")
      .data(nodes)
      .enter()
      .append("text")
      .text((d) => d.id)
      .attr("font-size", "12px")
      .attr("text-anchor", "middle")
      .attr("dy", ".35em")
      .attr("fill", "white");

    function ticked() {
      link
        .attr("x1", (d) => d.source.x)
        .attr("y1", (d) => d.source.y)
        .attr("x2", (d) => d.target.x)
        .attr("y2", (d) => d.target.y);

      node.each(constrainBounds).attr("cx", (d) => d.x).attr("cy", (d) => d.y);

      label.attr("x", (d) => d.x).attr("y", (d) => d.y);
    }

    function dragStarted(event, d) {
      if (!event.active) simulation.alphaTarget(0.3).restart();
      d.fx = d.x;
      d.fy = d.y;
    }

    function dragged(event, d) {
      d.fx = event.x;
      d.fy = event.y;
    }

    function dragEnded(event, d) {
      if (!event.active) simulation.alphaTarget(0);
      d.fx = null;
      d.fy = null;
    }

    return () => simulation.stop();
  }, [nodes, edges, isDirected]);

  return (
    <div className="graph-visualization-container">
      <h1>{isDirected ? "Directed" : "Undirected"} Graph Visualization</h1>

      <div className="button-container">
        <button
          className={`toggle-button ${isDirected ? "active" : ""}`}
          onClick={() => {
            setIsDirected(true);
            validateInput(input);
          }}
        >
          Directed
        </button>
        <button
          className={`toggle-button ${!isDirected ? "active" : ""}`}
          onClick={() => {
            setIsDirected(false);
            validateInput(input);
          }}
        >
          Undirected
        </button>
      </div>

      <div className="input-download-container">
        <textarea
          placeholder={`Enter edges (e.g., "0 1" or "1 2") one per line`}
          onChange={handleInputChange}
          className="input-container"
          value={input}
          rows="5"
        ></textarea>
        <div className="download-container">
          <button className="download-button" onClick={downloadGraph}>
            <img src={db} alt="Download" className="download-icon" />
          </button>
          <p className="download-txt">Download Graph</p>
        </div>
      </div>
      {error && <div className="error-message">{error}</div>}
      <div className="button-container">
        <button
          className="operation_button"
          onClick={() => {
            if (nodes.length > 0) {
              const startNode = nodes[0].id;
              const bfsResult = bfs(startNode);
              alert(`BFS Traversal: ${bfsResult.join(", ")}`);
            }
          }}
        >
          BFS
        </button>
        <button
          className="operation_button"
          onClick={() => {
            if (nodes.length > 0) {
              const startNode = nodes[0].id;
              const dfsResult = dfs(startNode);
              alert(`DFS Traversal: ${dfsResult.join(", ")}`);
            }
          }}
        >
          DFS
        </button>
      </div>
      <div
        className="graph-display"
        ref={graphContainerRef}
        style={{ alignSelf: "center", overflow: "auto", maxWidth: "100%", height: "350px" }}
      >
        <svg ref={svgRef} width="700" height="350" style={{ border: "1px solid black" }}></svg>
      </div>
    </div>
  );
};

export default GraphVisualization;
