import React, { useEffect, useRef, useState } from "react";
import * as d3 from "d3";
import "./GraphVisualization.css";

const GraphVisualization = () => {
  const [nodes, setNodes] = useState([]); // List of nodes
  const [edges, setEdges] = useState([]); // List of edges
  const [input, setInput] = useState(""); // Input state for edge list
  const [error, setError] = useState(""); // Error state for validation
  const [isDirected, setIsDirected] = useState(true); // Toggle between Directed and Undirected
  const [selectedNode, setSelectedNode] = useState(null); // Track selected nodes for edge creation

  const svgRef = useRef(); // Reference to the SVG element

  // Handle the input change
  const handleInputChange = (e) => {
    const value = e.target.value;
    setInput(value);
    validateInput(value);
  };

  // Validate and update graph data
  const validateInput = (input) => {
    try {
      const lines = input.trim().split("\n");
      const newNodes = [];
      const newEdges = [];

      lines.forEach((line) => {
        const [from, to] = line.trim().split(" ");

        if (from === undefined || to === undefined || isNaN(from) || isNaN(to)) {
          throw new Error("Invalid input format! Each line must have two integers.");
        }

        // Ensure nodes are unique
        if (!newNodes.some((node) => node.id === from)) {
          newNodes.push({ id: from });
        }
        if (!newNodes.some((node) => node.id === to)) {
          newNodes.push({ id: to });
        }

        // Add edges
        newEdges.push({ source: from, target: to });

        // If undirected, add the reverse edge
        if (!isDirected) {
          newEdges.push({ source: to, target: from });
        }
      });

      // Remove duplicate edges in undirected mode
      const uniqueEdges = newEdges.filter(
        (edge, index, self) =>
          index === self.findIndex((e) => e.source === edge.source && e.target === edge.target)
      );

      // Update states
      setNodes(newNodes);
      setEdges(uniqueEdges);
      setError("");
    } catch (error) {
      setError(error.message);
    }
  };

  // Handle node click to select nodes for edge creation
  const handleNodeClick = (nodeId) => {
    if (selectedNode === null) {
      setSelectedNode(nodeId); // Select the first node
    } else {
      // Create directed edge from selectedNode to the current nodeId
      if (selectedNode !== nodeId) {
        setEdges((prevEdges) => [
          ...prevEdges,
          { source: selectedNode, target: nodeId },
        ]);
      }
      setSelectedNode(null); // Deselect after creating the edge
    }
  };

  // Draw the graph using D3
  useEffect(() => {
    const svg = d3.select(svgRef.current);
    svg.selectAll("*").remove(); // Clear previous graph

    if (nodes.length === 0 || edges.length === 0) return;

    const width = 600;
    const height = 400;

    // Create a simulation with forces
    const simulation = d3
      .forceSimulation(nodes)
      .force("link", d3.forceLink(edges).id((d) => d.id).distance(100))
      .force("charge", d3.forceManyBody().strength(-300))
      .force("center", d3.forceCenter(width / 2, height / 2))
      .on("tick", ticked);

    // Add links (edges)
    const link = svg
      .append("g")
      .attr("class", "links")
      .selectAll("line")
      .data(edges)
      .enter()
      .append("line")
      .attr("stroke", "#333")
      .attr("stroke-width", 2)
      .attr("marker-end", isDirected ? "url(#arrowhead)" : null); // Add arrowhead for directed graphs

    // Add nodes
    const node = svg
      .append("g")
      .attr("class", "nodes")
      .selectAll("circle")
      .data(nodes)
      .enter()
      .append("circle")
      .attr("r", 20)
      .attr("fill", "#4CAF50")
      .attr("stroke", "black")
      .attr("stroke-width", 2)
      .call(
        d3
          .drag()
          .on("start", dragStarted)
          .on("drag", dragged)
          .on("end", dragEnded)
      )
      .on("click", (event, d) => handleNodeClick(d.id)); // Add click event to nodes

    // Add labels
    const label = svg
      .append("g")
      .attr("class", "labels")
      .selectAll("text")
      .data(nodes)
      .enter()
      .append("text")
      .text((d) => d.id)
      .attr("font-size", "12px")
      .attr("text-anchor", "middle")
      .attr("dy", ".35em")
      .attr("fill", "white");

    // Tick handler
    function ticked() {
      link
        .attr("x1", (d) => d.source.x)
        .attr("y1", (d) => d.source.y)
        .attr("x2", (d) => d.target.x)
        .attr("y2", (d) => d.target.y);

      node.attr("cx", (d) => d.x).attr("cy", (d) => d.y);

      label.attr("x", (d) => d.x).attr("y", (d) => d.y);
    }

    // Drag event handlers
    function dragStarted(event, d) {
      if (!event.active) simulation.alphaTarget(0.3).restart();
      d.fx = d.x;
      d.fy = d.y;
    }

    function dragged(event, d) {
      d.fx = event.x;
      d.fy = event.y;
    }

    function dragEnded(event, d) {
      if (!event.active) simulation.alphaTarget(0);
      d.fx = null;
      d.fy = null;
    }

    return () => simulation.stop();
  }, [nodes, edges, isDirected]);

  return (
    <div className="graph-visualization-container">
      <h1>{isDirected ? "Directed" : "Undirected"} Graph Visualization</h1>

      {/* Buttons for toggling between directed and undirected */}
      <div className="button-container">
        <button
          className={`toggle-button ${isDirected ? "active" : ""}`}
          onClick={() => {
            setIsDirected(true);
            validateInput(input); // Re-validate input to update graph
          }}
        >
          Directed
        </button>
        <button
          className={`toggle-button ${!isDirected ? "active" : ""}`}
          onClick={() => {
            setIsDirected(false);
            validateInput(input); // Re-validate input to update graph
          }}
        >
          Undirected
        </button>
      </div>

      <textarea
        placeholder={`Enter edges (e.g., "0 1" or "1 2") one per line`}
        onChange={handleInputChange}
        className="input-container"
        value={input}
        rows="5"
      ></textarea>
      {error && <div className="error-message">{error}</div>}

      <div className="graph-display">
        <svg ref={svgRef} width="600" height="400" style={{ border: "1px solid black" }}>
          {/* Define arrowhead marker */}
          <defs>
            <marker
              id="arrowhead"
              markerWidth="10"
              markerHeight="7"
              refX="10"
              refY="3.5"
              orient="auto"
              markerUnits="strokeWidth"
            >
              <polygon points="0 0, 10 3.5, 0 7" fill="#333" />
            </marker>
          </defs>
        </svg>
      </div>
    </div>
  );
};

export default GraphVisualization;
