import React, { useState, useRef, useEffect } from "react";
import "./InsertionSort.css";
import arrowUp from "./assets/25637.png"; // Pointer arrow image

const InsertionSort = () => {
  const [array, setArray] = useState([]);
  const [key, setKey] = useState(null);
  const [i, setI] = useState(1);
  const [j, setJ] = useState(null);
  const [isPaused, setIsPaused] = useState(false);
  const [status, setStatus] = useState("");
  const [highlightedStep, setHighlightedStep] = useState(0); // Current step in the algorithm
  const timeouts = useRef([]);
  const ANIMATION_DELAY = 1500;
  const pausedState = useRef({});

  const algorithmSteps = [
    "for (int i = 1; i < n; i++) {",
    "  int key = arr[i];",
    "  int j = i - 1;",
    "  while (j >= 0 && arr[j] > key) {",
    "    arr[j + 1] = arr[j];",
    "    j--;",
    "  }",
    "  arr[j + 1] = key;",
    "}"
  ];

  useEffect(() => {
    return () => {
      timeouts.current.forEach((timeout) => clearTimeout(timeout));
    };
  }, []);

  const resetSortingState = () => {
    setIsPaused(false);
    timeouts.current.forEach((timeout) => clearTimeout(timeout));
    timeouts.current = [];
    setArray([]);
    setKey(null);
    setI(1);
    setJ(null);
    setStatus("");
    setHighlightedStep(0);
  };

  const togglePause = () => {
    setIsPaused((prev) => {
      if (prev) {
        // Resume from the paused state
        resumeSorting();
      } else {
        // Pause the sorting by clearing the timeouts and saving the current state
        pausedState.current = { array, i, j, key };
        timeouts.current.forEach((timeout) => clearTimeout(timeout));
        timeouts.current = [];
      }
      return !prev;
    });
  };

  const resumeSorting = () => {
    const { array, i, j, key } = pausedState.current;
    setArray([...array]);
    setI(i);
    setJ(j);
    setKey(key);

    if (j !== null) {
      innerStep([...array], key, i, j);
    } else {
      step([...array], i);
    }
  };

  const startSorting = (inputArray) => {
    resetSortingState();
    const parsedArray = inputArray.split(" ").map(Number);

    if (parsedArray.some(isNaN)) {
      alert("Please enter valid numbers separated by spaces.");
      return;
    }

    setArray(parsedArray);
    setStatus("Starting sort...");
    setTimeout(() => step(parsedArray, 1), 500);
  };

  const step = (currentArr, currentI) => {
    if (isPaused || currentI >= currentArr.length) {
      if (currentI >= currentArr.length) {
        setStatus("Sorting complete!");
        // Reset the box sizes and color after completion
        resetAllBoxesToDefault();
      }
      return;
    }

    const key = currentArr[currentI];
    setKey(key);
    setJ(currentI - 1);
    setStatus(`Sorting element ${key}...`);
    setHighlightedStep(1); // Highlight the current algorithm step

    movePointer("i-pointer", currentI, "↑"); // Move i pointer

    // Highlight the current element and update it
    updateArray(currentArr, currentI, key, true);
    setTimeout(() => {
      setHighlightedStep(3); // Highlight the "while" condition part of the algorithm
      innerStep([...currentArr], key, currentI, currentI - 1);
    }, ANIMATION_DELAY);
  };

  const innerStep = (currentArr, key, currentI, currentJ) => {
    if (isPaused) return;

    if (currentJ >= 0 && currentArr[currentJ] > key) {
      currentArr[currentJ + 1] = currentArr[currentJ];
      updateArray(currentArr, currentJ + 1, currentArr[currentJ], true); // Highlight swap
      setJ(currentJ - 1);

      movePointer("j-pointer", currentJ, "↑"); // Move j pointer

      let timeoutId = setTimeout(() => innerStep([...currentArr], key, currentI, currentJ - 1), ANIMATION_DELAY);
      timeouts.current.push(timeoutId);
    } else {
      currentArr[currentJ + 1] = key;
      updateArray(currentArr, currentJ + 1, key, false); // Mark as sorted

      setStatus(`Inserted ${key} at position ${currentJ + 1}`);
      setI(currentI + 1);
      setJ(null);
      movePointer("j-pointer", -1, "↑"); // Hide j pointer after insertion

      let timeoutId = setTimeout(() => step([...currentArr], currentI + 1), ANIMATION_DELAY);
      timeouts.current.push(timeoutId);
    }
  };

  const updateArray = (newArr, index, value, highlight) => {
    setArray([...newArr]);

    const box = document.getElementById(`box-${index}`);
    if (box) {
      box.innerText = value;
      if (highlight) {
        box.classList.add("highlight"); // Add highlight
      } else {
        box.classList.remove("highlight");
        box.classList.add("sorted"); // Add sorted style
      }
    }
  };

  const movePointer = (pointerId, index, direction) => {
    const pointer = document.getElementById(pointerId);
    if (pointer) {
      if (index === -1) {
        pointer.style.display = "none"; // Hide pointer when not in use
      } else {
        pointer.style.display = "block"; // Show pointer
        pointer.innerHTML = `<img src="${arrowUp}" alt="arrow" style="width: 24px; height: 24px;" /><br/>${pointerId[0]} = ${index}`;

        const box = document.getElementById(`box-${index}`);
        if (box) {
          pointer.style.left = `${box.offsetLeft + box.offsetWidth / 2 - 12}px`;
          pointer.style.top = `${box.offsetTop + box.offsetHeight + 10}px`; // Adjust pointer position
        }
      }
    }
  };

  const resetAllBoxesToDefault = () => {
    setArray((prevArray) => prevArray.map((num) => num));

    const allBoxes = document.querySelectorAll(".box");
    allBoxes.forEach((box) => {
      box.classList.remove("highlight");
      box.classList.add("sorted");
      box.style.transform = "scale(1)";
    });
  };

  return (
    <div className="insertion-sort-container">
      <h1>Insertion Sort</h1>
      <div className="controls">
        <input
          id="arrayInput"
          type="text"
          placeholder="Enter numbers separated by space"
        />
        <button id="sortButton" onClick={() => startSorting(document.getElementById("arrayInput").value)}>
          Start Sorting
        </button>
        <button id="pauseButton" onClick={togglePause}>
          {isPaused ? "Resume Animation" : "Pause Animation"}
        </button>
      </div>

      <div id="visualization" style={{ display: "flex", gap: "10px", position: "relative" }}>
        {array.map((num, idx) => (
          <div key={idx} id={`box-${idx}`} className="box">
            {num}
          </div>
        ))}
        <div id="i-pointer" className="pointer">i</div>
        <div id="j-pointer" className="pointer">j</div>
      </div>

      <div id="status">{status}</div>

      <div className="algorithm">
        {algorithmSteps.map((step, index) => (
          <pre
            key={index}
            className={`algorithm-step ${index === highlightedStep ? "highlight-text" : ""}`}
          >
            {step}
          </pre>
        ))}
      </div>
    </div>
  );
};

export default InsertionSort;
