import React, { useState, useEffect, useRef } from "react";
import "./InsertionSort.css";

const InsertionSort = () => {
  const [array, setArray] = useState([]); // The array to sort
  const [i, setI] = useState(1); // Outer loop index
  const [j, setJ] = useState(null); // Inner loop index
  const [isPaused, setIsPaused] = useState(false); // Pause state
  const [status, setStatus] = useState(""); // Status display
  const timeouts = useRef([]); // Ref to store active timeouts

  useEffect(() => {
    // Cleanup timeouts on unmount
    return () => {
      timeouts.current.forEach((timeout) => clearTimeout(timeout));
    };
  }, []);

  const resetSortingState = () => {
    setIsPaused(false);
    timeouts.current.forEach((timeout) => clearTimeout(timeout));
    timeouts.current = [];
    setArray([]);
    setI(1);
    setJ(null);
    setStatus("");
  };

  const togglePause = () => {
    setIsPaused((prevPaused) => {
      if (!prevPaused) {
        // Pause
        timeouts.current.forEach((timeout) => clearTimeout(timeout));
        timeouts.current = [];
      } else {
        // Resume
        if (j !== null) {
          innerStep([...array], i, j);
        } else {
          step([...array], i);
        }
      }
      return !prevPaused;
    });
  };

  const startSorting = (input) => {
    resetSortingState();

    // Parse input
    const parsedArray = input.split(" ").map(Number);
    if (parsedArray.some(isNaN)) {
      alert("Please enter valid numbers separated by spaces.");
      return;
    }

    setArray(parsedArray);
    setStatus("Starting sort...");
    setTimeout(() => step(parsedArray, 1), 500); // Start sorting with animation delay
  };

  const step = (currentArr, currentI) => {
    if (isPaused || currentI >= currentArr.length) {
      if (currentI >= currentArr.length) {
        setStatus("Sorting complete!");
      }
      return;
    }

    const key = currentArr[currentI];
    setStatus(`Sorting element ${key}...`);
    setI(currentI);
    setJ(currentI - 1);

    innerStep([...currentArr], currentI, currentI - 1);
  };

  const innerStep = (currentArr, currentI, currentJ) => {
    if (isPaused) return;

    const key = currentArr[currentI];

    if (currentJ >= 0 && currentArr[currentJ] > key) {
      // Shift element
      currentArr[currentJ + 1] = currentArr[currentJ];
      setArray([...currentArr]);
      setJ(currentJ - 1);

      timeouts.current.push(
        setTimeout(() => innerStep(currentArr, currentI, currentJ - 1), 500)
      );
    } else {
      // Insert the key
      currentArr[currentJ + 1] = key;
      setArray([...currentArr]);
      setStatus(`Inserted ${key} at position ${currentJ + 1}`);
      setJ(null);

      // Move to the next step
      timeouts.current.push(setTimeout(() => step(currentArr, currentI + 1), 500));
    }
  };

  return (
    <div className="insertion-sort-container">
      <h1>Insertion Sort Visualization</h1>
      <input
        type="text"
        placeholder="Enter numbers separated by spaces"
        id="arrayInput"
      />
      <button
        id="sortButton"
        onClick={() =>
          startSorting(document.getElementById("arrayInput").value)
        }
      >
        Start Sorting
      </button>
      <button id="pauseButton" onClick={togglePause}>
        {isPaused ? "Resume Animation" : "Pause Animation"}
      </button>
      <div id="visualization" className="visualization">
        {array.map((num, index) => (
          <div
            key={index}
            className={`box ${
              index === i ? "highlight" : index < i ? "sorted" : ""
            }`}
          >
            {num}
          </div>
        ))}
      </div>
      <div id="status" className="status">{status}</div>
    </div>
  );
};

export default InsertionSort;
