import React, { useState, useEffect, useRef } from 'react';
import './InsertionSort.css';

const InsertionSort = () => {
  const [array, setArray] = useState([]);
  const [isPaused, setIsPaused] = useState(false);
  const [i, setI] = useState(1);
  const [j, setJ] = useState(0);
  const [key, setKey] = useState(null);
  const [status, setStatus] = useState('');
  const [sortedArray, setSortedArray] = useState([]);
  const pointerRef = useRef(null); // Ref for the pointer

  const [inputValue, setInputValue] = useState('');
  const [currentTimeouts, setCurrentTimeouts] = useState([]);
  const [isSorting, setIsSorting] = useState(false);

  // Function to reset the sorting state
  const resetSortingState = () => {
    setIsPaused(false);
    setIsSorting(false);
    setCurrentTimeouts([]);
    setArray([]);
    setSortedArray([]);
    setI(1);
    setJ(0);
    setKey(null);
    setStatus('');
  };

  // Function to update the pointer position
  const updatePointer = () => {
    const currentBox = document.getElementById(`box-${i}`);
    if (currentBox && pointerRef.current) {
      const offsetX = currentBox.offsetLeft + currentBox.offsetWidth / 2 - 30;
      const offsetY = currentBox.offsetTop + currentBox.offsetHeight;
      pointerRef.current.style.left = `${offsetX}px`;
      pointerRef.current.style.top = `${offsetY}px`;
    }
  };

  // Function to toggle between pause and resume
  const togglePause = () => {
    setIsPaused(prev => !prev);
  };

  // Function to continue sorting from where it was paused
  const continueSorting = () => {
    step(); // Resume sorting
  };

  const step = () => {
    if (isPaused) return;

    if (i >= array.length) {
      setSortedArray([...array]);
      setStatus('Sorting complete!');
      setIsSorting(false);
      return;
    }

    setKey(array[i]);
    setJ(i - 1);
    setStatus(`Sorting element ${array[i]}...`);
    updatePointer();

    innerStep(array[i]);
  };

  const innerStep = (key) => {
    if (isPaused) return;

    if (j >= 0 && array[j] > key) {
      const newArray = [...array];
      newArray[j + 1] = array[j];
      setArray(newArray);
      setJ(j - 1);

      setTimeout(() => innerStep(key), 1000); // Continue inner loop after timeout
    } else {
      const newArray = [...array];
      newArray[j + 1] = key;
      setArray(newArray);

      setStatus(`Inserted ${key} at position ${j + 1}`);
      setI(i + 1);
      setTimeout(step, 1000); // Continue to the next element after timeout
    }
  };

  const startSorting = () => {
    if (!inputValue) {
      setStatus('Please enter an array to sort.');
      return;
    }
    resetSortingState();
    const parsedArray = inputValue.split(' ').map(Number);
    setArray(parsedArray);
    setStatus('');
    setIsSorting(true);
    step();
  };

  // Watch for changes to i (the index being processed)
  useEffect(() => {
    if (pointerRef.current) {
      pointerRef.current.style.display = 'block';
    }
  }, [i]);

  return (
    <div className="insertion-sort-container">
      <h1>Insertion Sort</h1>
      <input
        type="text"
        value={inputValue}
        onChange={(e) => setInputValue(e.target.value)}
        placeholder="Enter numbers separated by space"
      />
      <div className="button-container">
        <button onClick={startSorting} disabled={isSorting}>
          Start Sorting
        </button>
        <button onClick={togglePause}>{isPaused ? 'Resume' : 'Pause'}</button>
      </div>

      <div id="visualization">
        {array.map((num, idx) => (
          <div
            key={idx}
            id={`box-${idx}`}
            className={`box ${num === key ? 'highlight' : ''} ${sortedArray.includes(num) ? 'sorted' : ''}`}
          >
            {num}
          </div>
        ))}
      </div>

      <div id="status">{status}</div>

      {/* Pointer */}
      <div id="pointer" ref={pointerRef} style={{ position: 'absolute', display: 'none' }}>
        â†‘
      </div>

      <div id="index">i = {i}</div>
    </div>
  );
};

export default InsertionSort;
