import React, { useState, useEffect, useRef } from 'react';
import './InsertionSort.css';

const InsertionSort = () => {
  const [array, setArray] = useState([]);
  const [isPaused, setIsPaused] = useState(false);
  const [i, setI] = useState(1);
  const [j, setJ] = useState(0);
  const [key, setKey] = useState(null);
  const [status, setStatus] = useState('');
  const [isSorting, setIsSorting] = useState(false);
  const pointerRef = useRef(null);  // Ref for pointer

  const [inputValue, setInputValue] = useState('');

  // Function to reset the sorting state
  const resetSortingState = () => {
    setIsPaused(false);
    setIsSorting(false);
    setArray([]);
    setI(1);
    setJ(0);
    setKey(null);
    setStatus('');
  };

  // Update pointer position
  const updatePointer = () => {
    const currentBox = document.getElementById(`box-${i}`);
    if (currentBox && pointerRef.current) {
      const offsetX = currentBox.offsetLeft + currentBox.offsetWidth / 2 - 30;
      const offsetY = currentBox.offsetTop + currentBox.offsetHeight;
      pointerRef.current.style.left = `${offsetX}px`;
      pointerRef.current.style.top = `${offsetY}px`;
    }
  };

  // Function to start sorting
  const startSorting = () => {
    if (!inputValue) {
      setStatus('Please enter an array to sort.');
      return;
    }
    resetSortingState();
    const parsedArray = inputValue.split(' ').map(Number);
    setArray(parsedArray);
    setStatus('');
    setIsSorting(true);
    step();
  };

  // Sorting step function
  const step = () => {
    if (isPaused) return;

    if (i >= array.length) {
      setStatus('Sorting complete!');
      return;
    }

    setKey(array[i]);
    setJ(i - 1);
    setStatus(`Sorting element ${array[i]}...`);
    updatePointer();  // Update pointer position

    innerStep(array[i]);
  };

  // Inner step for insertion sorting
  const innerStep = (key) => {
    if (isPaused) return;

    if (j >= 0 && array[j] > key) {
      const newArray = [...array];
      newArray[j + 1] = array[j];
      setArray(newArray);
      setJ(j - 1);

      setTimeout(() => innerStep(key), 1000);  // Continue inner loop
    } else {
      const newArray = [...array];
      newArray[j + 1] = key;
      setArray(newArray);

      setStatus(`Inserted ${key} at position ${j + 1}`);
      setI(i + 1);
      setTimeout(step, 1000);  // Continue to next step
    }
  };

  // Watch for changes to the array to trigger visualization updates
  useEffect(() => {
    if (isSorting) {
      step();
    }
  }, [i, j, array, isPaused, isSorting]);

  return (
    <div className="insertion-sort-container">
      <div className="container">
        <h1>Insertion Sort Visualization</h1>
        <input
          type="text"
          id="arrayInput"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          placeholder="Enter array (e.g., 5 2 8 3)"
        />
        <div id="visualization">
          {array.map((value, index) => (
            <div
              key={index}
              id={`box-${index}`}
              className={`box ${index === j ? 'highlight' : ''} ${index === i ? 'sorted' : ''}`}
            >
              {value}
            </div>
          ))}
        </div>
        <div id="pointer" ref={pointerRef}>
          <span id="arrow">â†’</span>
        </div>
        <div id="status">{status}</div>
        <button onClick={startSorting} disabled={isSorting}>
          Start Sorting
        </button>
        <button onClick={() => setIsPaused(!isPaused)}>
          {isPaused ? 'Resume' : 'Pause'}
        </button>
      </div>
    </div>
  );
};

export default InsertionSort;
