import React, { useState, useRef, useEffect } from "react";
import "./BubbleSort.css";

const BubbleSort = () => {
  const [array, setArray] = useState([]);
  const [isPaused, setIsPaused] = useState(false);
  const [status, setStatus] = useState("");
  const timeouts = useRef([]);
  const ANIMATION_DELAY = 1000; // Animation delay in ms

  useEffect(() => {
    return () => {
      // Cleanup timeouts when component unmounts
      timeouts.current.forEach((timeout) => clearTimeout(timeout));
    };
  }, []);

  const resetSortingState = () => {
    timeouts.current.forEach((timeout) => clearTimeout(timeout));
    timeouts.current = [];
    setArray([]);
    setIsPaused(false);
    setStatus("");
  };

  const startSorting = (inputArray) => {
    resetSortingState();
    const parsedArray = inputArray.split(" ").map(Number);

    if (parsedArray.some(isNaN)) {
      alert("Please enter valid numbers separated by spaces.");
      return;
    }

    setArray(parsedArray);
    setStatus("Starting sort...");
    setTimeout(() => bubbleSort(parsedArray), 500);
  };

  const bubbleSort = (arr) => {
    let n = arr.length;
    let swapped = false;
    let steps = [];

    // Prepare the steps for animation
    for (let i = 0; i < n - 1; i++) {
      for (let j = 0; j < n - i - 1; j++) {
        if (arr[j] > arr[j + 1]) {
          // Swap elements
          [arr[j], arr[j + 1]] = [arr[j + 1], arr[j]];
          swapped = true;
        }
        steps.push([...arr]); // Record array state
      }
      if (!swapped) break;
    }

    // Start animating the steps
    animateSteps(steps);
  };

  const animateSteps = (steps) => {
    steps.forEach((step, index) => {
      let timeoutId = setTimeout(() => {
        setArray(step);

        if (index === steps.length - 1) {
          setStatus("Sorting complete!");
        }
      }, ANIMATION_DELAY * index);

      timeouts.current.push(timeoutId);
    });
  };

  const togglePause = () => {
    setIsPaused((prev) => {
      if (prev) {
        // Resume
        setStatus("Resuming...");
        // No actual animation resume here as we're using timeouts
      } else {
        setStatus("Paused");
        // Clear all pending timeouts
        timeouts.current.forEach((timeout) => clearTimeout(timeout));
        timeouts.current = [];
      }
      return !prev;
    });
  };

  return (
    <div className="bubble-sort-container">
      <h1>Bubble Sort</h1>
      <div className="controls">
        <input
          id="arrayInput"
          type="text"
          placeholder="Enter numbers separated by space"
        />
        <button
          id="sortButton"
          onClick={() =>
            startSorting(document.getElementById("arrayInput").value)
          }
        >
          Start Sorting
        </button>
        <button id="pauseButton" onClick={togglePause}>
          {isPaused ? "Resume" : "Pause"}
        </button>
      </div>

      <div className="visualization-container">
        {array.map((num, idx) => (
          <div key={idx} className="box" style={{ height: `${num * 5}px` }}>
            {num}
          </div>
        ))}
      </div>

      <div id="status">{status}</div>
    </div>
  );
};

export default BubbleSort;
