import React, { useState } from "react";
import ReactFlow, { Controls, ReactFlowProvider } from "reactflow";
import "reactflow/dist/style.css";

const GraphVisualizer = () => {
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  const [inputValue, setInputValue] = useState("");

  const parseInput = () => {
    const lines = inputValue.trim().split("\n");
    const newNodes = [];
    const nodeIds = new Set(); // To track node IDs
    const newEdges = [];

    lines.forEach((line) => {
      const parts = line.split(" ").map((item) => item.trim());

      // Handle nodes (single number per line)
      if (parts.length === 1) {
        const nodeId = parts[0];
        if (!nodeIds.has(nodeId)) {
          nodeIds.add(nodeId);
          newNodes.push({
            id: nodeId,
            data: { label: `Node ${nodeId}` },
            position: { x: Math.random() * 400, y: Math.random() * 400 }, // Random position
            style: { borderRadius: "50%", background: "#3a8dff", color: "white", padding: "10px" }, // Circle style for nodes
          });
        }
      }

      // Handle edges (source and target node, optionally weight)
      if (parts.length >= 2) {
        const [source, target, weight] = parts;
        if (nodeIds.has(source) && nodeIds.has(target)) {
          newEdges.push({
            id: `${source}-${target}`,
            source,
            target,
            label: weight ? `Weight: ${weight}` : "",
            animated: false, // No animation for undirected edges
          });
        } else {
          console.warn(`Edge (${source} -> ${target}) refers to non-existent nodes.`);
        }
      }
    });

    setNodes(newNodes);
    setEdges(newEdges);
  };

  return (
    <ReactFlowProvider>
      <div style={{ padding: "20px" }}>
        <textarea
          placeholder="Enter graph input..."
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          rows="6"
          cols="30"
          style={{ marginBottom: "10px", display: "block" }}
        />
        <button onClick={parseInput} style={{ marginRight: "10px" }}>
          Generate Graph
        </button>
        <div style={{ width: "100%", height: "400px", marginTop: "20px", border: "1px solid #ddd" }}>
          <ReactFlow nodes={nodes} edges={edges} fitView>
            <Controls />
          </ReactFlow>
        </div>
      </div>
    </ReactFlowProvider>
  );
};

export default GraphVisualizer;
