import React, { useState, useRef, useEffect } from "react";
import "./InsertionSort.css";

const InsertionSort = () => {
  const [array, setArray] = useState([]);
  const [key, setKey] = useState(null); // Variable to store the value being inserted
  const [i, setI] = useState(1);
  const [j, setJ] = useState(null);
  const [isPaused, setIsPaused] = useState(false);
  const [status, setStatus] = useState("");
  const timeouts = useRef([]);

  useEffect(() => {
    // Cleanup timeouts on component unmount
    return () => {
      timeouts.current.forEach((timeout) => clearTimeout(timeout));
    };
  }, []);

  const resetSortingState = () => {
    setIsPaused(false);
    timeouts.current.forEach((timeout) => clearTimeout(timeout));
    timeouts.current = [];
    setArray([]);
    setKey(null);
    setI(1);
    setJ(null);
    setStatus("");
  };

  const togglePause = () => {
    setIsPaused((prev) => {
      if (!prev) {
        // Pause
        timeouts.current.forEach((timeout) => clearTimeout(timeout));
        timeouts.current = [];
      } else {
        // Resume
        if (j !== null) {
          innerStep([...array], key, i, j);
        } else {
          step([...array], i);
        }
      }
      return !prev;
    });
  };

  const startSorting = (inputArray) => {
    resetSortingState();
    const parsedArray = inputArray.split(" ").map(Number);

    if (parsedArray.some(isNaN)) {
      alert("Please enter valid numbers separated by spaces.");
      return;
    }

    setArray(parsedArray);
    setStatus("Starting sort...");
    setTimeout(() => step(parsedArray, 1), 500);
  };

  const step = (currentArr, currentI) => {
    if (isPaused || currentI >= currentArr.length) {
      if (currentI >= currentArr.length) {
        setStatus("Sorting complete!");
      }
      return;
    }

    const key = currentArr[currentI];
    setKey(key); // Store the current value to be inserted
    setStatus(`Sorting element ${key}...`);
    setI(currentI);
    setJ(currentI - 1);

    // Start the inner loop
    innerStep([...currentArr], key, currentI, currentI - 1);
  };

  const innerStep = (currentArr, key, currentI, currentJ) => {
    if (isPaused) return;

    if (currentJ >= 0 && currentArr[currentJ] > key) {
      currentArr[currentJ + 1] = currentArr[currentJ]; // Shift element to the right
      setArray([...currentArr]); // Update array for visualization
      setJ(currentJ - 1);

      timeouts.current.push(
        setTimeout(() => innerStep(currentArr, key, currentI, currentJ - 1), 500)
      );
    } else {
      currentArr[currentJ + 1] = key; // Insert key in the correct position
      setArray([...currentArr]);
      setStatus(`Inserted ${key} at position ${currentJ + 1}`);
      setJ(null);

      // Move to the next element in the outer loop
      timeouts.current.push(setTimeout(() => step(currentArr, currentI + 1), 500));
    }
  };

  return (
    <div className="insertion-sort-container">
      <h1>Insertion Sort Visualization</h1>
      <input
        id="arrayInput"
        type="text"
        placeholder="Enter numbers separated by spaces"
      />
      <button
        id="sortButton"
        onClick={() =>
          startSorting(document.getElementById("arrayInput").value)
        }
      >
        Start Sorting
      </button>
      <button id="pauseButton" onClick={togglePause}>
        {isPaused ? "Resume Animation" : "Pause Animation"}
      </button>
      <div id="visualization" className="visualization">
        {array.map((num, index) => (
          <div
            key={index}
            className={`box ${
              index < i ? "sorted" : index === i ? "highlight" : ""
            }`}
          >
            {num}
          </div>
        ))}
      </div>
      <div id="status" className="status">
        {status}
      </div>
    </div>
  );
};

export default InsertionSort;
