import React, { useState } from 'react';
import './InsertionSort.css'; // Make sure this is the correct path

function InsertionSort() {
  const [array, setArray] = useState([]);
  const [isPaused, setIsPaused] = useState(false);
  const [status, setStatus] = useState('');
  const [i, setI] = useState(1);
  const [j, setJ] = useState(0);
  const [currentTimeouts, setCurrentTimeouts] = useState([]);
  const [sortedArray, setSortedArray] = useState([]);
  
  // Track the current key being sorted
  const [key, setKey] = useState(null);

  // Reset sorting state
  const resetSortingState = () => {
    setIsPaused(false);
    currentTimeouts.forEach(timeout => clearTimeout(timeout)); // Clear all timeouts
    setCurrentTimeouts([]);
    setArray([]);
    setSortedArray([]);
    setI(1);
    setJ(0);
    setKey(null);
    setStatus('');
  };

  const togglePause = () => {
    setIsPaused(prev => !prev);
    if (isPaused) {
      continueSorting();
    } else {
      currentTimeouts.forEach(timeout => clearTimeout(timeout));
      setCurrentTimeouts([]);
    }
  };

  const continueSorting = () => {
    step();
  };

  const step = () => {
    if (isPaused) return;

    if (i >= array.length) {
      setStatus('Sorting complete!');
      setSortedArray([...array]); // Set the sorted array
      return;
    }

    setKey(array[i]);
    setJ(i - 1);
    setStatus(`Sorting element ${array[i]}...`);

    // Continue inner loop step
    innerStep(array[i]);
  };

  const innerStep = (key) => {
    if (isPaused) return;

    if (j >= 0 && array[j] > key) {
      const newArray = [...array];
      newArray[j + 1] = array[j];
      setArray(newArray);
      setJ(j - 1);
      setTimeout(() => innerStep(key), 1000); // Continue inner step after timeout
    } else {
      const newArray = [...array];
      newArray[j + 1] = key;
      setArray(newArray);
      setStatus(`Inserted ${key} at position ${j + 1}`);
      setI(i + 1);
      setTimeout(step, 1000); // Continue to the next element after timeout
    }
  };

  const startSorting = () => {
    resetSortingState();
    const arrayInput = prompt('Enter numbers separated by space');
    const parsedArray = arrayInput.split(' ').map(Number);
    setArray(parsedArray);
    setI(1);
    setStatus('');
    step();
  };

  return (
    <div className="insertion-sort-container">
      <h1>Insertion Sort</h1>
      <button onClick={startSorting}>Start Sorting</button>
      <button onClick={togglePause}>{isPaused ? 'Resume' : 'Pause'}</button>

      {/* Visualization */}
      <div id="visualization">
        {array.map((num, idx) => (
          <div
            key={idx}
            className={`box ${num === key ? 'highlight' : ''} ${sortedArray.includes(num) ? 'sorted' : ''}`}
          >
            {num}
          </div>
        ))}
      </div>

      <div id="status">{status}</div>
    </div>
  );
}

export default InsertionSort;
