import React, { useState, useEffect, useRef } from 'react';
import './InsertionSort.css';

function InsertionSortVisualizer() {
  const [arr, setArr] = useState([]);
  const [isPaused, setIsPaused] = useState(false);
  const [i, setI] = useState(1);  // The current index for insertion sort
  const [j, setJ] = useState(0);  // The inner loop index for insertion sort
  const [currentTimeouts, setCurrentTimeouts] = useState([]);
  const [status, setStatus] = useState('');
  const [sorted, setSorted] = useState([]);

  // Function to reset the sorting state
  const resetSortingState = () => {
    setIsPaused(false);
    setCurrentTimeouts([]);
    setSorted([]);
    setI(1);  // Start from the first element
    setJ(0);  // Reset inner loop index
    setStatus('');
  };

  // Function to toggle between pause and resume
  const togglePause = () => {
    setIsPaused(!isPaused);
  };

  // Function to continue sorting from where it was paused
  const continueSorting = () => {
    step();
  };

  // Function to start the sorting
  const startSorting = (arrayInput) => {
    resetSortingState();
    const parsedArr = arrayInput.split(' ').map(Number);
    if (parsedArr.some(isNaN)) {
      alert('Please enter valid numbers separated by spaces.');
      return;
    }
    setArr(parsedArr);
    setSorted(new Array(parsedArr.length).fill(false));  // Initialize sorted array
    step();
  };

  const updatePointer = () => {
    // Pointer update logic based on i (position of pointer will be handled by CSS)
  };

  const updateIndexDisplay = () => {
    return `i = ${i}`;
  };

  // Sorting steps for the animation
  const step = () => {
    if (isPaused) return;

    if (i >= arr.length) {
      // Mark all items as sorted
      setSorted(arr.map(() => true));
      setStatus('Sorting complete!');
      return;
    }

    let key = arr[i];
    let newJ = i - 1;
    const newArr = [...arr];
    setStatus(`Sorting element ${key}...`);

    const innerStep = () => {
      if (isPaused) return;

      if (newJ >= 0 && newArr[newJ] > key) {
        newArr[newJ + 1] = newArr[newJ];
        setArr([...newArr]);
        newJ--;
        setJ(newJ);

        // Call innerStep with timeout
        setCurrentTimeouts((prevTimeouts) => [
          ...prevTimeouts,
          setTimeout(innerStep, 1500),
        ]);
      } else {
        newArr[newJ + 1] = key;
        setArr([...newArr]);
        setStatus(`Inserted ${key} at position ${newJ + 1}`);

        setI((prevI) => prevI + 1);  // Move to the next element
        setJ(newJ);

        // Call step with timeout
        setCurrentTimeouts((prevTimeouts) => [
          ...prevTimeouts,
          setTimeout(step, 1500),
        ]);
      }
    };

    innerStep();
  };

  useEffect(() => {
    // Cleanup timeouts when component unmounts
    return () => {
      currentTimeouts.forEach((timeout) => clearTimeout(timeout));
    };
  }, [currentTimeouts]);

  return (
    <div className="insertion-sort-container">
      <div>
        <input
          type="text"
          id="arrayInput"
          placeholder="Enter numbers separated by spaces"
          onChange={(e) => startSorting(e.target.value)}
        />
      </div>

      <div id="visualization">
        {arr.map((num, index) => (
          <div
            key={index}
            className={`box ${sorted[index] ? 'sorted' : ''}`}
            id={`box-${index}`}
          >
            {num}
          </div>
        ))}
      </div>

      <div id="status">{status}</div>

      <div id="pointer">→</div>

      <div id="index">{updateIndexDisplay()}</div>

      <button onClick={togglePause}>
        {isPaused ? 'Resume Animation' : 'Pause Animation'}
      </button>
    </div>
  );
}

export default InsertionSortVisualizer;
