import React, { useEffect, useRef, useState } from "react";
import * as d3 from "d3";
import { toPng } from "html-to-image"; // Library for converting HTML to image
import "./GraphVisualization.css";
import db from "./assets/down-arrow_752650.png";

const GraphVisualization = () => {
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  const [input, setInput] = useState("");
  const [error, setError] = useState("");
  const [isDirected, setIsDirected] = useState(true);
  const [queueState, setQueueState] = useState([]); // Track BFS queue state
  const [visitedNodes, setVisitedNodes] = useState(new Set()); // Track visited nodes
  const [topoSortResult, setTopoSortResult] = useState("");
 

  const svgRef = useRef();
  const graphContainerRef = useRef(); // Ref for the graph container

  const handleInputChange = (e) => {
    const value = e.target.value;
    setInput(value);
    validateInput(value);
  };

  const validateInput = (input) => {
    try {
      const lines = input.trim().split("\n");
      const newNodes = [];
      const newEdges = [];
  
      lines.forEach((line) => {
        const parts = line.trim().split(" ");
        if (parts.length < 2 || isNaN(parts[0]) || isNaN(parts[1])) {
          throw new Error("Invalid input format! Each line must have two integers, and optionally a weight.");
        }
  
        const [from, to, weight] = parts;
        if (!newNodes.some((node) => node.id === from)) newNodes.push({ id: from });
        if (!newNodes.some((node) => node.id === to)) newNodes.push({ id: to });
  
        // Handle optional weight
        const edgeWeight = weight ? parseInt(weight) : null;  // Set to null if no weight provided
        newEdges.push({ source: from, target: to, weight: edgeWeight });
  
        if (!isDirected) {
          newEdges.push({ source: to, target: from, weight: edgeWeight });
        }
      });
  
      const uniqueEdges = newEdges.filter(
        (edge, index, self) =>
          index ===
          self.findIndex(
            (e) =>
              (e.source === edge.source && e.target === edge.target) ||
              (e.source === edge.target && e.target === edge.source)
          )
      );
  
      setNodes(newNodes);
      setEdges(uniqueEdges);
      setError("");
    } catch (error) {
      setError(error.message);
    }
  };
  

  const downloadGraph = () => {
    if (graphContainerRef.current) {
      toPng(graphContainerRef.current)
        .then((dataUrl) => {
          const link = document.createElement("a");
          link.download = "graph_visualization.png";
          link.href = dataUrl;
          link.click();
        })
        .catch((err) => {
          console.error("Failed to download graph as PNG:", err);
        });
    }
  };

  useEffect(() => {
    const svg = d3.select(svgRef.current);
    svg.selectAll("*").remove();

    if (nodes.length === 0 || edges.length === 0) return;

    const width = 700;
    const height = 350;

    const simulation = d3
    .forceSimulation(nodes)
    .force("link", d3.forceLink(edges).id((d) => d.id).distance(140)) // Increase edge distance here (was 100)
    .force("charge", d3.forceManyBody().strength(-300))
    .force("center", d3.forceCenter(width / 2, height / 2))
    .force("x", d3.forceX(width / 2).strength(0.1))
    .force("y", d3.forceY(height / 2).strength(0.1))
    .on("tick", ticked);
  

    function constrainBounds(d) {
      d.x = Math.max(20, Math.min(width - 20, d.x));
      d.y = Math.max(20, Math.min(height - 20, d.y));
    }

    svg
      .append("defs")
      .append("marker")
      .attr("id", "arrowhead")
      .attr("viewBox", "0 -5 10 10")
      .attr("refX", 25)
      .attr("refY", 0)
      .attr("orient", "auto")
      .attr("markerWidth", 6)
      .attr("markerHeight", 6)
      .append("path")
      .attr("d", "M0,-5L10,0L0,5")
      .attr("fill", "#42a5f5");

    const link = svg
      .append("g")
      .attr("class", "links")
      .selectAll("line")
      .data(edges)
      .enter()
      .append("line")
      .attr("stroke", "#333")
      .attr("stroke-width", 2)
      .attr("marker-end", isDirected ? "url(#arrowhead)" : null);

      const node = svg
      .append("g")
      .attr("class", "nodes")
      .selectAll("circle")
      .data(nodes)
      .enter()
      .append("circle")
      .attr("r", 20)
      .attr("fill", "#4CAF50")
      .attr("stroke", "black")
      .attr("stroke-width", 2)
      .attr("class", (d) => `node node-${d.id}`) // Add unique class for each node
      .call(
        d3
          .drag()
          .on("start", dragStarted)
          .on("drag", dragged)
          .on("end", dragEnded)
      );
    

    const label = svg
      .append("g")
      .attr("class", "labels")
      .selectAll("text")
      .data(nodes)
      .enter()
      .append("text")
      .text((d) => d.id)
      .attr("font-size", "12px")
      .attr("text-anchor", "middle")
      .attr("dy", ".35em")
      .attr("fill", "white");

      const edgeLabels = svg
      .append("g")
      .attr("class", "edge-labels")
      .selectAll("text")
      .data(edges)
      .enter()
      .append("text")
      .text((d) => (d.weight !== null ? d.weight : "")) // Display weight only if it's not null
      .attr("font-size", "12px")
      .attr("text-anchor", "middle")
      .attr("dy", "-10px")
      .attr("fill", "black")  // Ensure this is explicitly set
      .style("fill", "black"); // Explicitly set fill using style for stronger application
    

    function ticked() {
      link
        .attr("x1", (d) => d.source.x)
        .attr("y1", (d) => d.source.y)
        .attr("x2", (d) => d.target.x)
        .attr("y2", (d) => d.target.y);

      node.each(constrainBounds).attr("cx", (d) => d.x).attr("cy", (d) => d.y);

      label.attr("x", (d) => d.x).attr("y", (d) => d.y);
      edgeLabels
        .attr("x", (d) => (d.source.x + d.target.x) / 2)
        .attr("y", (d) => (d.source.y + d.target.y) / 2);
    }

    function dragStarted(event, d) {
      if (!event.active) simulation.alphaTarget(0.3).restart();
      d.fx = d.x;
      d.fy = d.y;
    }

    function dragged(event, d) {
      d.fx = event.x;
      d.fy = event.y;
    }

    function dragEnded(event, d) {
      if (!event.active) simulation.alphaTarget(0);
      d.fx = null;
      d.fy = null;
    }

    return () => simulation.stop();
  }, [nodes, edges, isDirected]);

  const handleBFS = () => {
    const startNode = nodes[0].id; // Start from the first node (can be customized)
    const queue = [startNode];
    const visited = new Set();
    visited.add(startNode);
  
    const bfsTraversal = (prevNode = null) => {
      if (queue.length === 0) {
        // Remove highlight for the last processed node
        if (prevNode) d3.select(`.node-${prevNode}`).classed("highlighted", false);
        return;
      }
  
      const currentNode = queue.shift(); // Dequeue node
      setQueueState((prevQueueState) => [...prevQueueState, currentNode]);
      setVisitedNodes((prevVisitedNodes) => new Set(prevVisitedNodes.add(currentNode)));
  
      // Remove highlight for the previously processed node
      if (prevNode) {
        d3.select(`.node-${prevNode}`).classed("highlighted", false);
      }
  
      // Highlight the current node
      d3.select(`.node-${currentNode}`).classed("highlighted", true);
  
      // Find neighbors by comparing ids, as edges have source/target as objects
      const neighbors = edges
        .filter(
          (edge) =>
            edge.source.id === currentNode || edge.target.id === currentNode
        )
        .map((edge) =>
          edge.source.id === currentNode ? edge.target.id : edge.source.id
        );
  
      neighbors.forEach((neighbor) => {
        if (!visited.has(neighbor)) {
          visited.add(neighbor);
          queue.push(neighbor);
        }
      });
  
      setTimeout(() => bfsTraversal(currentNode), 500); // Delay for visualization
    };
  
    bfsTraversal(); // Start the BFS traversal
  };
  const handleDFS = () => {
    const startNode = nodes[0].id; // Start from the first node (can be customized)
    const visited = new Set();
  
    const dfsTraversal = (currentNode, prevNode = null) => {
      if (visited.has(currentNode)) return;
  
      visited.add(currentNode);
      setVisitedNodes((prevVisitedNodes) => new Set(prevVisitedNodes.add(currentNode)));
  
      // Remove highlight for the previously processed node
      if (prevNode) {
        d3.select(`.node-${prevNode}`).classed("highlighted", false);
      }
  
      // Highlight the current node
      d3.select(`.node-${currentNode}`).classed("highlighted", true);
  
      // Find neighbors
      const neighbors = edges
        .filter(
          (edge) =>
            edge.source.id === currentNode || edge.target.id === currentNode
        )
        .map((edge) =>
          edge.source.id === currentNode ? edge.target.id : edge.source.id
        );
  
      // Delay for visualization and recursively explore neighbors
      setTimeout(() => {
        neighbors.forEach((neighbor) => {
          if (!visited.has(neighbor)) {
            dfsTraversal(neighbor, currentNode);
          }
        });
  
        // After exploring all neighbors, remove highlight from the current node
        if (!neighbors.length && prevNode !== null) {
          d3.select(`.node-${currentNode}`).classed("highlighted", false);
        }
      }, 500);
    };
  
    dfsTraversal(startNode); // Start DFS traversal
  };
  const handleTopoSort = () => {
    // Step 1: Calculate in-degrees for each node
    const inDegree = {};
    nodes.forEach(node => {
      inDegree[node.id] = 0;
    });
  
    edges.forEach(edge => {
      inDegree[edge.target.id] = (inDegree[edge.target.id] || 0) + 1;
    });
  
    // Step 2: Initialize the queue with nodes having in-degree of 0
    const queue = [];
    nodes.forEach(node => {
      if (inDegree[node.id] === 0) {
        queue.push(node.id);
      }
    });
  
    let topologicalOrder = [];
    let visitedCount = 0;
  
    // Step 3: Process nodes in the queue
    while (queue.length > 0) {
      const currentNode = queue.shift(); // Dequeue node
  
      topologicalOrder.push(currentNode); // Add it to topological order
      visitedCount++;
  
      // Find all neighbors (edges) of the current node and decrease their in-degree
      edges.forEach(edge => {
        if (edge.source.id === currentNode) {
          const neighbor = edge.target.id;
          inDegree[neighbor]--;
  
          // If in-degree of neighbor becomes 0, add it to the queue
          if (inDegree[neighbor] === 0) {
            queue.push(neighbor);
          }
        }
      });
    }
  
    // Step 4: Check if there was a cycle (graph is not a DAG)
    if (visitedCount !== nodes.length) {
      setError("The graph is not a Directed Acyclic Graph (DAG). Topological Sort not possible.");
      return;
    }
  
    // Step 5: Display the topological order
    setError(""); // Clear previous errors
    setTopoSortResult(topologicalOrder.join(" → "));
  };
  
  const findShortestPath = (startNode, endNode) => {
    if (!startNode || !endNode) {
      setError("Please provide both start and end nodes.");
      return;
    }
  
    const graph = new Map();
  
    // Build adjacency list with weights
    edges.forEach(({ source, target, weight }) => {
      if (!graph.has(source)) graph.set(source, []);
      graph.get(source).push({ node: target, weight });
      if (!isDirected) {
        if (!graph.has(target)) graph.set(target, []);
        graph.get(target).push({ node: source, weight });
      }
    });
  
    // Initialize distances and previous nodes
    const distances = {};
    const previousNodes = {};
    const unvisitedNodes = new Set(nodes.map(({ id }) => id));
  
    nodes.forEach(({ id }) => {
      distances[id] = id === startNode ? 0 : Infinity;
      previousNodes[id] = null;
    });
  
    // Dijkstra's Algorithm
    while (unvisitedNodes.size > 0) {
      // Find the unvisited node with the smallest distance
      const currentNode = [...unvisitedNodes].reduce((closest, node) =>
        distances[node] < distances[closest] ? node : closest
      );
  
      unvisitedNodes.delete(currentNode);
  
      // Stop if we reach the endNode
      if (currentNode === endNode) break;
  
      // Update distances for neighbors
      const neighbors = graph.get(currentNode) || [];
      for (const { node: neighbor, weight } of neighbors) {
        const newDistance = distances[currentNode] + weight;
        if (newDistance < distances[neighbor]) {
          distances[neighbor] = newDistance;
          previousNodes[neighbor] = currentNode;
        }
      }
    }
  
    // Reconstruct the shortest path
    const path = [];
    let currentNode = endNode;
  
    while (currentNode) {
      path.unshift(currentNode);
      currentNode = previousNodes[currentNode];
    }
  
    if (path[0] !== startNode) {
      setError(`No path exists between ${startNode} and ${endNode}.`);
      setShortestPathResult("");
    } else {
      setError("");
      setShortestPathResult(path.join(" → "));
    }
  };
  
  
  
  
  
  return (
    <div className="graph-visualization-container">
      <h1>{isDirected ? "Directed" : "Undirected"} Graph Visualization</h1>

      <div className="button-container">
        <button
          className={`toggle-button ${isDirected ? "active" : ""}`}
          onClick={() => {
            setIsDirected(true);
            validateInput(input);
          }}
        >
          Directed
        </button>
        <button
          className={`toggle-button ${!isDirected ? "active" : ""}`}
          onClick={() => {
            setIsDirected(false);
            validateInput(input);
          }}
        >
          Undirected
        </button>
      </div>

      <div className="input-download-container">
        <textarea
          placeholder={`Enter edges (e.g., "1 2 3" for edge with weight 3) one per line`}
          onChange={handleInputChange}
          className="input-container"
          value={input}
          rows="5"
        ></textarea>
        <div className="download-container">
          <button className="download-button" onClick={downloadGraph}>
            <img src={db} alt="Download" className="download-icon" />
          </button>
          <p className="download-txt">Download Graph</p>
        </div>
      </div>
      {error && <div className="error-message">{error}</div>}
      <div className="graphdislpay-button-wraper">
      <div className="operation-button-container">
      <button className="operation_button" onClick={() => console.log("BFS invoked")}>
        BFS
      </button>
      <button className="operation_button" onClick={() => console.log("DFS invoked")}>
        DFS
      </button>
      <button className="operation_button" onClick={() => console.log("Topo Sort invoked")}>
        Topo Sort
      </button>

      <div style={{ display: "inline-block", position: "relative" }}>
        <button
          className="operation_button"
          onClick={() => setShowShortestPathInputs((prev) => !prev)}
        >
          Shortest Path
        </button>
        {showShortestPathInputs && (
          <div style={{ display: "inline-flex", alignItems: "center", marginLeft: "10px" }}>
            <input
              type="text"
              placeholder="Start Node"
              onChange={(e) =>
                setShortestPathInput((prev) => ({
                  ...prev,
                  startNode: e.target.value,
                }))
              }
              style={{ marginRight: "10px" }}
            />
            <input
              type="text"
              placeholder="End Node"
              onChange={(e) =>
                setShortestPathInput((prev) => ({
                  ...prev,
                  endNode: e.target.value,
                }))
              }
              style={{ marginRight: "10px" }}
            />
            <button
              className="operation_button"
              onClick={() =>
                findShortestPath(shortestPathInput.startNode, shortestPathInput.endNode)
              }
            >
              Start
            </button>
          </div>
        )}
      </div>

      <button className="operation_button" onClick={() => console.log("MST invoked")}>
        MST
      </button>
      <button className="operation_button" onClick={() => console.log("Kruskal invoked")}>
        Kruskal
      </button>
    </div>
  <div
    className="graph-display"
    ref={graphContainerRef}
    style={{ alignItems: "flex-end", overflow: "auto", maxWidth: "63%", height: "400px" }}
  >
    <svg ref={svgRef} width="1000" height="400" style={{ border: "1px solid black" }}></svg>
  </div>
</div>
{topoSortResult && (
    <div className="operation-result">
      <h3>Topological Sort:</h3>
      <p>{topoSortResult}</p>
    </div>
  )}
   {shortestPathResult && (
        <div className="operation-result">
          <h3>Shortest Path:</h3>
          <p>{shortestPathResult}</p>
        </div>
      )}

  </div>
  );
};

export default GraphVisualization;
