import React, { useState } from "react";
import "./BubbleSort.css";

const BubbleSort = () => {
  const [array, setArray] = useState([]);
  const [isPaused, setIsPaused] = useState(false);
  const [status, setStatus] = useState("");
  const [timeouts, setTimeouts] = useState([]);

  const resetSortingState = () => {
    setIsPaused(false);
    timeouts.forEach((timeout) => clearTimeout(timeout));
    setTimeouts([]);
    setArray([]);
    setStatus("");
  };

  const togglePause = () => {
    setIsPaused((prev) => !prev);
    if (!isPaused) {
      // Clear timeouts to pause
      timeouts.forEach((timeout) => clearTimeout(timeout));
      setTimeouts([]);
      setStatus("Paused...");
    } else {
      // Resume sorting
      setStatus("Resuming...");
      performBubbleSort([...array]);
    }
  };

  const startSorting = () => {
    resetSortingState();
    const arrayInput = prompt("Enter numbers separated by space");
    if (!arrayInput) return;

    const parsedArray = arrayInput.split(" ").map(Number);
    if (parsedArray.some(isNaN)) {
      alert("Please enter valid numbers separated by spaces.");
      return;
    }

    setArray(parsedArray);
    setStatus("Starting sort...");
    performBubbleSort(parsedArray);
  };

  const performBubbleSort = (arr) => {
    const steps = [];
    const n = arr.length;

    for (let i = 0; i < n; i++) {
      for (let j = 0; j < n - i - 1; j++) {
        if (arr[j] > arr[j + 1]) {
          [arr[j], arr[j + 1]] = [arr[j + 1], arr[j]]; // Swap
          steps.push([...arr]); // Push current array state for animation
        }
      }
    }

    animateSorting(steps);
  };

  const animateSorting = (steps) => {
    const newTimeouts = steps.map((step, index) => {
      return setTimeout(() => {
        setArray(step);
        if (index === steps.length - 1) {
          setStatus("Sorting complete!");
        }
      }, index * 500); // 500ms delay between steps
    });

    setTimeouts(newTimeouts);
  };

  return (
    <div className="bubble-sort-container">
      <h1>Bubble Sort</h1>
      <div className="controls">
        <button onClick={startSorting}>Start Sorting</button>
        <button onClick={togglePause}>{isPaused ? "Resume" : "Pause"}</button>
      </div>
      <div className="visualization-container">
        {array.map((num, idx) => (
          <div
            key={idx}
            className="box"
            style={{ height: `${num * 5}px` }}
          >
            {num}
          </div>
        ))}
      </div>
      <div className="status">{status}</div>
    </div>
  );
};

export default BubbleSort;
