import React, { useState } from 'react';

function BubbleSort() {
  const [array, setArray] = useState([]);
  const [isPaused, setIsPaused] = useState(false);
  const [status, setStatus] = useState('');
  const [i, setI] = useState(0);
  const [currentTimeouts, setCurrentTimeouts] = useState([]);
  
  const resetSortingState = () => {
    setIsPaused(false);
    currentTimeouts.forEach(timeout => clearTimeout(timeout));
    setCurrentTimeouts([]);
    setArray([]);
    setI(0);
    setStatus('');
  };

  const togglePause = () => {
    setIsPaused(prev => !prev);
    if (isPaused) {
      continueSorting();
    } else {
      currentTimeouts.forEach(timeout => clearTimeout(timeout));
      setCurrentTimeouts([]);
    }
  };

  const continueSorting = () => {
    step();
  };

  const step = () => {
    if (isPaused) return;

    if (i >= array.length) {
      setStatus('Sorting complete!');
      return;
    }

    let j = 0;
    const innerStep = () => {
      if (j < array.length - 1 - i) {
        if (array[j] > array[j + 1]) {
          [array[j], array[j + 1]] = [array[j + 1], array[j]]; // Swap
          setArray([...array]);
        }
        j++;
        setTimeout(innerStep, 1000);
      } else {
        setI(prevI => prevI + 1);
        step();
      }
    };
    innerStep();
  };

  const startSorting = () => {
    resetSortingState();
    const arrayInput = prompt("Enter numbers separated by space");
    const parsedArray = arrayInput.split(' ').map(Number);
    setArray(parsedArray);
    setI(0);
    setStatus('');
    step();
  };

  return (
    <div>
      <h1>Bubble Sort</h1>
      <button onClick={startSorting}>Sort</button>
      <button onClick={togglePause}>{isPaused ? "Resume" : "Pause"}</button>
      <div id="visualization">
        {array.map((num, idx) => (
          <div key={idx} className="box">{num}</div>
        ))}
      </div>
      <div>{status}</div>
    </div>
  );
}

export default BubbleSort;
