import React, { useState } from "react";
import "./TreeVisualisation.css";

const TreeVisualizer = () => {
  const [input, setInput] = useState('[1,2,3,4,5,null,8,null,null,6,7,9]');
  const [treeData, setTreeData] = useState(null);

  // Function to build the tree from a level-order array
  const buildTree = (arr) => {
    if (!arr.length) return null;

    const nodes = arr.map((value) => (value !== null ? { value, left: null, right: null } : null));

    for (let i = 0, j = 1; j < arr.length; i++) {
      if (nodes[i] !== null) {
        if (j < arr.length) nodes[i].left = nodes[j++];
        if (j < arr.length) nodes[i].right = nodes[j++];
      }
    }

    return nodes[0];
  };

  // Handle "Visualize Tree" button click
  const handleVisualize = () => {
    try {
      const parsedArray = JSON.parse(input);
      setTreeData(buildTree(parsedArray));
    } catch (error) {
      alert("Invalid input format. Please enter a valid JSON array.");
    }
  };

  // Recursive function to render the tree nodes and lines
  const renderTree = (node, x, y, level, parentX = null, parentY = null) => {
    if (!node) return null;

    // Dynamically adjust baseSpacing based on the level
    const baseSpacing = level > 2 ? 300 : 300; // Use 600 for levels > 4, otherwise 400
    const horizontalSpacing = baseSpacing / Math.pow(2, level + 1); // Spacing reduces as depth increases

    const leftChildX = x - horizontalSpacing;
    const rightChildX = x + horizontalSpacing;
    const childY = y + 100; // Vertical distance between levels

    return (
      <>
        {/* Draw line to parent node */}
        {parentX !== null && (
          <line
            x1={parentX}
            y1={parentY + 20}
            x2={x}
            y2={y - 20}
            stroke="black"
            strokeWidth="2"
          />
        )}
        {/* Draw current node */}
        <g className="circle-group">
          <circle cx={x} cy={y} r="20" className="tree-circle" />
          <text x={x} y={y} textAnchor="middle" dy="5" fill="white" fontWeight="bold">
            {node.value}
          </text>
        </g>
        {/* Render left and right child nodes recursively */}
        {node.left && renderTree(node.left, leftChildX, childY, level + 1, x, y)}
        {node.right && renderTree(node.right, rightChildX, childY, level + 1, x, y)}
      </>
    );
  };

  // Handle "Download Tree" button click
  const handleDownload = () => {
    const svgElement = document.getElementById("treeCanvas");
    const svgData = new XMLSerializer().serializeToString(svgElement);
    const blob = new Blob([svgData], { type: "image/svg+xml" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "tree.svg";
    a.click();
  };

  return (
    <div className="tree-visualizer-container">
      <h1 className="title">Tree Visualizer</h1>

      {/* Textarea for input */}
      <textarea
        className="input-box"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder="Enter a level-order array, e.g., [1,2,3,4,5,null,8,null,null,6,7,9]"
      />

      {/* Buttons for actions */}
      <div className="button-container">
        <button className="visualize-button" onClick={handleVisualize}>
          Visualize Tree
        </button>
        <button className="download-button" onClick={handleDownload}>
          Download Tree
        </button>
      </div>

      {/* Canvas for rendering the tree */}
      <div className="canvas-container">
        <svg id="treeCanvas" className="tree-canvas" viewBox="0 0 1000 800">
          {treeData && renderTree(treeData, 500, 50, 0)} {/* Root starts at center */}
        </svg>
      </div>
    </div>
  );
};

export default TreeVisualizer;
