import React, { useState, useEffect } from "react";
import "./TreeVisualisation.css";

const TreeVisualizer = () => {
  const [input, setInput] = useState('[1,2,3,4,5,null,8,null,null,6,7,9]');
  const [treeData, setTreeData] = useState(null);
  const [treeDimensions, setTreeDimensions] = useState({ width: 0, height: 0 });

  // Build the tree from a level-order array
  const buildTree = (arr) => {
    if (!arr.length) return null;

    const nodes = arr.map((value) =>
      value !== null ? { value, left: null, right: null } : null
    );

    for (let i = 0, j = 1; j < arr.length; i++) {
      if (nodes[i] !== null) {
        if (j < arr.length) nodes[i].left = nodes[j++];
        if (j < arr.length) nodes[i].right = nodes[j++];
      }
    }

    return nodes[0];
  };

  const handleVisualize = () => {
    try {
      const parsedArray = JSON.parse(input);
      setTreeData(buildTree(parsedArray));
    } catch (error) {
      alert("Invalid input format. Please enter a valid JSON array.");
    }
  };

  // Calculate horizontal and vertical positions dynamically based on subtree width and height
  const calculateNodePositions = (node, depth = 0, x = 0, positions = {}) => {
    if (!node) return 0;

    const spacing = 150; // Spacing between nodes horizontally
    const leftWidth = calculateNodePositions(node.left, depth + 1, x, positions);
    const rightWidth = calculateNodePositions(node.right, depth + 1, x + leftWidth + spacing, positions);

    const totalWidth = leftWidth + rightWidth + spacing;
    const currentX = x + leftWidth + spacing / 2;
    positions[node.value] = { x: currentX, y: depth * 150 + 50 };

    return totalWidth;
  };

  // Render the tree with dynamically calculated positions
  const renderTree = (node, positions, parentX = null, parentY = null) => {
    if (!node) return null;

    const { x, y } = positions[node.value];
    
    return (
      <>
        {/* Draw line to parent node */}
        {parentX !== null && parentY !== null && (
          <line
            x1={parentX}
            y1={parentY}
            x2={x}
            y2={y}
            stroke="black"
            strokeWidth="2"
          />
        )}
        {/* Draw node */}
        <g className="circle-group">
          <circle cx={x} cy={y} r={35} fill="green" stroke="black" />
          <text x={x} y={y} textAnchor="middle" dy="5" fill="white">
            {node.value}
          </text>
        </g>
        {/* Recursive rendering of children */}
        {renderTree(node.left, positions, x, y)}
        {renderTree(node.right, positions, x, y)}
      </>
    );
  };

  const handleDownload = () => {
    const svgElement = document.getElementById("treeCanvas");
    const svgData = new XMLSerializer().serializeToString(svgElement);
    const blob = new Blob([svgData], { type: "image/svg+xml" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "tree.svg";
    a.click();
  };

  // Generate positions for the nodes
  const positions = {};
  if (treeData) {
    const width = calculateNodePositions(treeData, 0, 500, positions); // Start at x=500
    const height = Object.values(positions).reduce((maxY, { y }) => Math.max(maxY, y), 0);
    setTreeDimensions({ width, height });
  }

  return (
    <div className="tree-visualizer-container">
      <h1 className="title">Tree Visualizer</h1>
      <textarea
        className="input-box"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder="Enter a level-order array, e.g., [1,2,3,4,5,null,8,null,null,6,7,9]"
      />
      <div className="button-container">
        <button className="visualize-button" onClick={handleVisualize}>
          Visualize Tree
        </button>
        <button className="download-button" onClick={handleDownload}>
          Download Tree
        </button>
      </div>
      <div className="canvas-container">
        <svg
          id="treeCanvas"
          className="tree-canvas"
          viewBox={`0 0 ${treeDimensions.width + 200} ${treeDimensions.height + 200}`}
          width="100%"
          height="100vh"
        >
          {treeData && renderTree(treeData, positions)}
        </svg>
      </div>
    </div>
  );
};

export default TreeVisualizer;
