import React, { useState } from "react";
import "./GraphVisualization.css";

const GraphVisualization = () => {
  const [nodes, setNodes] = useState([]); // List of nodes
  const [edges, setEdges] = useState([]); // List of edges
  const [positions, setPositions] = useState({}); // Store node positions
  const [input, setInput] = useState(""); // Input state for adjacency list
  const [error, setError] = useState(""); // Error state for validation

  // Handle the input change
  const handleInputChange = (e) => {
    const value = e.target.value;
    setInput(value);
    validateInput(value);
  };

  // Validate and update graph data
  const validateInput = (input) => {
    try {
      const adjList = JSON.parse(input);

      // Validate if the input is an array of arrays
      if (!Array.isArray(adjList) || adjList.some((item) => !Array.isArray(item))) {
        setError("Invalid format! Please enter a valid adjacency list.");
        return;
      }

      // Initialize new nodes and edges
      const newNodes = [];
      const newEdges = [];

      adjList.forEach((neighbors, from) => {
        const fromId = from.toString(); // Ensure node id is a string

        // Add the source node if not already present
        if (!newNodes.some((node) => node.id === fromId)) {
          newNodes.push({ id: fromId });
        }

        neighbors.forEach((to) => {
          const toId = to.toString();

          // Add the target node if not already present
          if (!newNodes.some((node) => node.id === toId)) {
            newNodes.push({ id: toId });
          }

          // Add the edge if not already present
          if (!newEdges.some((edge) => edge.from === fromId && edge.to === toId)) {
            newEdges.push({ from: fromId, to: toId });
          }
        });
      });

      // Update states
      setNodes(newNodes);
      setEdges(newEdges);
      setError("");

      // Apply positions and update the graph
      applyPositions(newNodes);
    } catch (error) {
      setError("Invalid JSON! Please provide a valid adjacency list.");
    }
  };

  // Assign random positions to nodes
  const applyPositions = (newNodes) => {
    const width = 600;
    const height = 400;

    const newPositions = { ...positions };

    newNodes.forEach((node) => {
      if (!newPositions[node.id]) {
        newPositions[node.id] = {
          x: Math.random() * width,
          y: Math.random() * height,
        };
      }
    });

    setPositions(newPositions);
  };

  return (
    <div className="graph-visualization-container">
      <h1>Directed Graph Visualization</h1>
      <input
        type="text"
        placeholder='Enter adjacency list as JSON (e.g., [[1,2],[3,4]])'
        onChange={handleInputChange}
        className="input-container"
        value={input}
      />

      {error && <div className="error-message">{error}</div>} {/* Display error message if any */}

      <div className="graph-display">
        <svg width="600" height="400" style={{ border: "1px solid black" }}>
          {/* Define arrowhead marker */}
          <defs>
            <marker
              id="arrowhead"
              markerWidth="10"
              markerHeight="7"
              refX="10"
              refY="3.5"
              orient="auto"
              markerUnits="strokeWidth"
            >
              <polygon points="0 0, 10 3.5, 0 7" fill="#333" />
            </marker>
          </defs>

          {/* Render edges */}
          {edges.map((edge, index) => {
            const fromPos = positions[edge.from];
            const toPos = positions[edge.to];

            if (!fromPos || !toPos) return null;

            // Calculate adjusted positions to avoid arrows overlapping nodes
            const dx = toPos.x - fromPos.x;
            const dy = toPos.y - fromPos.y;
            const distance = Math.sqrt(dx * dx + dy * dy);
            const offset = 30; // Node radius offset

            const startX = fromPos.x + (dx / distance) * offset;
            const startY = fromPos.y + (dy / distance) * offset;
            const endX = toPos.x - (dx / distance) * offset;
            const endY = toPos.y - (dy / distance) * offset;

            return (
              <line
                key={index}
                x1={startX}
                y1={startY}
                x2={endX}
                y2={endY}
                stroke="#333"
                strokeWidth={2}
                markerEnd="url(#arrowhead)" // Add arrowhead to edge
              />
            );
          })}

          {/* Render nodes */}
          {nodes.map((node) => {
            const pos = positions[node.id];
            if (!pos) return null;

            return (
              <g key={node.id}>
                <circle
                  cx={pos.x}
                  cy={pos.y}
                  r={30}
                  fill="#4CAF50"
                  stroke="black"
                  strokeWidth={3}
                >
                  <title>{node.id}</title>
                </circle>
                {/* Render the node's name inside the circle */}
                <text
                  x={pos.x}
                  y={pos.y}
                  fontSize="16"
                  fontWeight="bold"
                  textAnchor="middle"
                  fill="white"
                  dy=".3em" // Vertically center the text
                >
                  {node.id}
                </text>
              </g>
            );
          })}
        </svg>
      </div>
    </div>
  );
};

export default GraphVisualization;
