import React, { useState } from "react";
import "./TreeVisualisation.css";

const TreeVisualizer = () => {
  const [input, setInput] = useState('[1,2,3,4,5,null,8,null,null,6,7,9]');
  const [treeData, setTreeData] = useState(null);

  // Function to build the tree from a level-order array
  const buildTree = (arr) => {
    if (!arr.length) return null;

    const nodes = arr.map((value) => (value !== null ? { value, left: null, right: null } : null));

    for (let i = 0, j = 1; j < arr.length; i++) {
      if (nodes[i] !== null) {
        if (j < arr.length) nodes[i].left = nodes[j++];
        if (j < arr.length) nodes[i].right = nodes[j++];
      }
    }

    return nodes[0];
  };

  // Handle "Visualize Tree" button click
  const handleVisualize = () => {
    try {
      const parsedArray = JSON.parse(input);
      setTreeData(buildTree(parsedArray));
    } catch (error) {
      alert("Invalid input format. Please enter a valid JSON array.");
    }
  };

  // Recursive function to render the tree nodes and lines
  const renderTree = (node, x, y, level, positions, parentX = null, parentY = null) => {
    if (!node) return null;

    const radius = 30; // Circle radius
    const verticalSpacing = 100; // Vertical spacing between levels

    // Retrieve the calculated x-coordinate for this node
    const currentX = positions[level].shift();

    return (
      <>
        {/* Line connecting to parent */}
        {parentX !== null && (
          <line
            x1={parentX}
            y1={parentY + radius}
            x2={currentX}
            y2={y - radius}
            stroke="black"
            strokeWidth="2"
          />
        )}
        {/* Circle and text */}
        <g className="circle-group">
          <circle cx={currentX} cy={y} r={radius} className="tree-circle" />
          <text x={currentX} y={y} textAnchor="middle" dy="5" fill="white" fontWeight="bold">
            {node.value}
          </text>
        </g>
        {node.left && renderTree(node.left, x, y + verticalSpacing, level + 1, positions, currentX, y)}
        {node.right &&
          renderTree(node.right, x, y + verticalSpacing, level + 1, positions, currentX, y)}
      </>
    );
  };

  // Calculate node positions for each level to ensure no overlap
  const calculatePositions = (root, canvasWidth) => {
    const queue = [{ node: root, level: 0 }];
    const positions = [];
    const levelCounts = [];

    // Perform a BFS to determine the number of nodes at each level
    while (queue.length) {
      const { node, level } = queue.shift();
      if (!node) continue;

      if (!positions[level]) {
        positions[level] = [];
        levelCounts[level] = 0;
      }
      levelCounts[level]++;

      queue.push({ node: node.left, level: level + 1 });
      queue.push({ node: node.right, level: level + 1 });
    }

    // Calculate x-coordinates for nodes at each level
    const maxNodes = Math.max(...levelCounts);
    levelCounts.forEach((count, level) => {
      const spacing = canvasWidth / (count + 1);
      positions[level] = Array.from({ length: count }, (_, i) => (i + 1) * spacing);
    });

    return positions;
  };

  // Handle "Download Tree" button click
  const handleDownload = () => {
    const svgElement = document.getElementById("treeCanvas");
    const svgData = new XMLSerializer().serializeToString(svgElement);
    const blob = new Blob([svgData], { type: "image/svg+xml" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "tree.svg";
    a.click();
  };

  return (
    <div className="tree-visualizer-container">
      <h1 className="title">Tree Visualizer</h1>

      {/* Textarea for input */}
      <textarea
        className="input-box"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder="Enter a level-order array, e.g., [1,2,3,4,5,null,8,null,null,6,7,9]"
      />

      {/* Buttons for actions */}
      <div className="button-container">
        <button className="visualize-button" onClick={handleVisualize}>
          Visualize Tree
        </button>
        <button className="download-button" onClick={handleDownload}>
          Download Tree
        </button>
      </div>

      {/* Canvas for rendering the tree */}
      <div className="canvas-container">
        <svg id="treeCanvas" className="tree-canvas" viewBox="0 0 1000 600">
          {treeData &&
            renderTree(treeData, 500, 50, 0, calculatePositions(treeData, 1000))}
        </svg>
      </div>
    </div>
  );
};

export default TreeVisualizer;
