import React, { useState } from "react";
import "./TreeVisualisation.css";

const TreeVisualizer = () => {
  const [input, setInput] = useState('[1,2,3,4,5,null,8,null,null,6,7,9]');
  const [treeData, setTreeData] = useState(null);
  const [highlightedNodes, setHighlightedNodes] = useState([]);
  const [queue, setQueue] = useState([]); // To visualize the BFS queue

  // Function to build the tree from a level-order array
  const buildTree = (arr) => {
    if (!arr.length) return null;

    const nodes = arr.map((value) =>
      value !== null ? { value, left: null, right: null } : null
    );

    for (let i = 0, j = 1; j < arr.length; i++) {
      if (nodes[i] !== null) {
        if (j < arr.length) nodes[i].left = nodes[j++];
        if (j < arr.length) nodes[i].right = nodes[j++];
      }
    }

    return nodes[0];
  };

  // Handle "Visualize Tree" button click
  const handleVisualize = () => {
    try {
      const parsedArray = JSON.parse(input);
      setTreeData(buildTree(parsedArray));
    } catch (error) {
      alert("Invalid input format. Please enter a valid JSON array.");
    }
  };

  // Recursive function to render the tree nodes and lines
  const renderTree = (node, x, y, level, parentX = null, parentY = null) => {
    if (!node) return null;

    const horizontalSpacing = 3800 / Math.pow(2, level + 4);
    const leftChildX = x - horizontalSpacing;
    const rightChildX = x + horizontalSpacing;
    const childY = y + 100;

    return (
      <>
        {parentX !== null && (
          <line
            x1={parentX}
            y1={parentY + 30}
            x2={x}
            y2={y}
            stroke="black"
            strokeWidth="3"
          />
        )}
        <g className="circle-group">
          <circle
            cx={x}
            cy={y}
            r="25"
            className={
              highlightedNodes.includes(node.value)
                ? "highlighted-circle"
                : "tree-circle"
            }
          />
          <text
            x={x}
            y={y}
            textAnchor="middle"
            dy="5"
            fill="white"
            fontWeight="bold"
          >
            {node.value}
          </text>
        </g>
        {node.left && renderTree(node.left, leftChildX, childY, level + 1, x, y)}
        {node.right &&
          renderTree(node.right, rightChildX, childY, level + 1, x, y)}
      </>
    );
  };

  // BFS Traversal with queue visualization
  const bfsTraversalWithQueue = (node) => {
    const result = [];
    const queue = [node];

    let currentQueue = [...queue];
    setQueue(currentQueue.map((n) => n.value));

    const interval = setInterval(() => {
      if (queue.length > 0) {
        const current = queue.shift();
        setHighlightedNodes([current.value]);
        result.push(current);

        if (current.left) queue.push(current.left);
        if (current.right) queue.push(current.right);

        // Update the queue visualization
        currentQueue = [...queue];
        setQueue(currentQueue.map((n) => n.value));
      } else {
        clearInterval(interval);
        setHighlightedNodes([]); // Clear highlights after traversal
        setQueue([]); // Clear queue visualization
      }
    }, 1000); // 1-second delay for visualization
  };

  const handleBFSVisualize = () => {
    if (treeData) bfsTraversalWithQueue(treeData);
    else alert("Please create the tree first.");
  };

  return (
    <div className="tree-visualizer-container">
      <h1 className="title">Tree Visualizer</h1>

      <textarea
        className="input-box"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder="Enter a level-order array, e.g., [1,2,3,4,5,null,8,null,null,6,7,9]"
      />

      <div className="button-container">
        <button className="visualize-button" onClick={handleVisualize}>
          Visualize Tree
        </button>
        <button className="download-button" onClick={handleBFSVisualize}>
          BFS
        </button>
      </div>

      <div className="visualization-container">
        {/* Tree Canvas */}
        <div className="canvas-container">
          <svg id="treeCanvas" className="tree-canvas" viewBox="0 0 1000 600">
            {treeData && renderTree(treeData, 500, 50, 0)}
          </svg>
        </div>

        {/* BFS Queue Visualization */}
        <div className="queue-container">
          <h2>Queue</h2>
          <div className="queue">
            {queue.map((value, index) => (
              <div key={index} className="queue-item">
                {value}
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default TreeVisualizer;
