import React, { useState, useRef, useEffect } from "react";
import "./InsertionSort.css";
import arrowUp from './assets/25637.png';

const InsertionSort = () => {
  const [array, setArray] = useState([]);
  const [key, setKey] = useState(null);
  const [i, setI] = useState(1);
  const [j, setJ] = useState(null);
  const [isPaused, setIsPaused] = useState(false);
  const [status, setStatus] = useState("");
  const [highlightedLine, setHighlightedLine] = useState(null);
  const timeouts = useRef([]);
  const ANIMATION_DELAY = 1500;

  useEffect(() => {
    return () => {
      timeouts.current.forEach((timeout) => clearTimeout(timeout));
    };
  }, []);

  const resetSortingState = () => {
    setIsPaused(false);
    timeouts.current.forEach((timeout) => clearTimeout(timeout));
    timeouts.current = [];
    setArray([]);
    setKey(null);
    setI(1);
    setJ(null);
    setStatus("");
    setHighlightedLine(null);
  };

  const togglePause = () => {
    setIsPaused((prev) => {
      if (prev) {
        resumeSorting();
      } else {
        timeouts.current.forEach((timeout) => clearTimeout(timeout));
        timeouts.current = [];
      }
      return !prev;
    });
  };

  const startSorting = (inputArray) => {
    resetSortingState();
    const parsedArray = inputArray.split(" ").map(Number);

    if (parsedArray.some(isNaN)) {
      alert("Please enter valid numbers separated by spaces.");
      return;
    }

    setArray(parsedArray);
    setStatus("Starting sort...");
    setHighlightedLine(1);
    setTimeout(() => step(parsedArray, 1), 500);
  };

  const step = (currentArr, currentI) => {
    if (isPaused || currentI >= currentArr.length) {
      if (currentI >= currentArr.length) {
        setStatus("Sorting complete!");
        resetAllBoxesToDefault();
      }
      return;
    }

    const key = currentArr[currentI];
    setKey(key);
    setJ(currentI - 1);
    setStatus(`Sorting element ${key}...`);
    setHighlightedLine(2);

    movePointer("i-pointer", currentI, "↑");
    updateArray(currentArr, currentI, key, true);

    innerStep([...currentArr], key, currentI, currentI - 1);
  };

  const innerStep = (currentArr, key, currentI, currentJ) => {
    if (isPaused) return;

    setHighlightedLine(3);
    if (currentJ >= 0 && currentArr[currentJ] > key) {
      currentArr[currentJ + 1] = currentArr[currentJ];
      updateArray(currentArr, currentJ + 1, currentArr[currentJ], true);
      setJ(currentJ - 1);
      movePointer("j-pointer", currentJ, "↑");

      let timeoutId = setTimeout(() => innerStep([...currentArr], key, currentI, currentJ - 1), ANIMATION_DELAY);
      timeouts.current.push(timeoutId);
    } else {
      currentArr[currentJ + 1] = key;
      updateArray(currentArr, currentJ + 1, key, false);
      setStatus(`Inserted ${key} at position ${currentJ + 1}`);
      setHighlightedLine(5);
      setI(currentI + 1);
      setJ(null);

      movePointer("j-pointer", -1, "↑");
      let timeoutId = setTimeout(() => step([...currentArr], currentI + 1), ANIMATION_DELAY);
      timeouts.current.push(timeoutId);
    }
  };

  const updateArray = (newArr, index, value, highlight) => {
    setArray([...newArr]);

    const box = document.getElementById(`box-${index}`);
    if (box) {
      box.innerText = value;
      if (highlight) {
        box.classList.add("highlight");
      } else {
        box.classList.remove("highlight");
        box.classList.add("sorted");
      }
    }
  };

  const movePointer = (pointerId, index, direction) => {
    const pointer = document.getElementById(pointerId);
    if (pointer) {
      if (index === -1) {
        pointer.style.display = "none";
      } else {
        pointer.style.display = "block";
        pointer.innerHTML = `
          <img src="${arrowUp}" alt="arrow" style="width: 24px; height: 24px;" />
          <br/> ${pointerId[0]} = ${index}`;
        const box = document.getElementById(`box-${index}`);
        if (box) {
          pointer.style.left = `${box.offsetLeft + box.offsetWidth / 2 - 12}px`;
          pointer.style.top = `${box.offsetTop + box.offsetHeight + 10}px`;
        }
      }
    }
  };

  const resetAllBoxesToDefault = () => {
    setArray((prevArray) => prevArray.map((num) => num));

    const allBoxes = document.querySelectorAll(".box");
    allBoxes.forEach((box) => {
      box.classList.remove("highlight");
      box.classList.add("sorted");
      box.style.transform = "scale(1)";
    });
  };

  const renderHighlightedAlgorithm = () => {
    const lines = [
      "Procedure InsertionSort(array)",
      "  For i = 1 to length(array) - 1",
      "    key = array[i]",
      "    j = i - 1",
      "    While j >= 0 AND array[j] > key",
      "      array[j + 1] = array[j]",
      "      j = j - 1",
      "    End While",
      "    array[j + 1] = key",
      "  End For",
      "End Procedure"
    ];

    return (
      <pre>
        {lines.map((line, index) => (
          <div key={index} className={highlightedLine === index + 1 ? "highlight-algo" : ""}>
            {line}
          </div>
        ))}
      </pre>
    );
  };

  return (
    <div className="insertion-sort-container">
      <h1>Insertion Sort</h1>
      <div className="controls">
        <input id="arrayInput" type="text" placeholder="Enter numbers separated by space" />
        <button onClick={() => startSorting(document.getElementById("arrayInput").value)}>
          Start Sorting
        </button>
        <button onClick={togglePause}>
          {isPaused ? "Resume Animation" : "Pause Animation"}
        </button>
      </div>
      <div id="visualization" style={{ display: "flex", gap: "10px", position: "relative" }}>
        {array.map((num, idx) => (
          <div key={idx} id={`box-${idx}`} className="box">
            {num}
          </div>
        ))}
        <div id="i-pointer" className="pointer">i</div>
        <div id="j-pointer" className="pointer">j</div>
      </div>
      <div id="algorithm">{renderHighlightedAlgorithm()}</div>
      <div id="status">{status}</div>
    </div>
  );
};

export default InsertionSort;
