import React, { useState, useEffect, useRef } from "react";
import * as d3 from "d3";

const GraphVisualization = () => {
  const [input, setInput] = useState("1 2\n2 3\n2 4\n3 5\n4 6"); // Default input
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  const [bfsStartNode, setBfsStartNode] = useState("");
  const [highlightedNodes, setHighlightedNodes] = useState([]);
  const [traversalQueue, setTraversalQueue] = useState([]);
  const [isTraversalRunning, setIsTraversalRunning] = useState(false);
  const svgRef = useRef();

  // Validate and parse the input graph
  const validateInput = (input) => {
    const lines = input.trim().split("\n");
    const parsedNodes = new Set();
    const parsedEdges = [];

    try {
      lines.forEach((line) => {
        const [from, to] = line.trim().split(" ").map(Number);
        if (isNaN(from) || isNaN(to)) {
          throw new Error("Input must consist of two integers per line.");
        }
        parsedNodes.add(from);
        parsedNodes.add(to);
        parsedEdges.push({ source: from, target: to });
      });

      const newNodes = Array.from(parsedNodes).map((id) => ({ id }));
      setNodes(newNodes);
      setEdges(parsedEdges);
    } catch (error) {
      alert(error.message);
    }
  };

  // BFS Traversal with highlighting
  const bfsTraversal = (startNodeId) => {
    if (!nodes.some((node) => node.id === +startNodeId)) {
      alert(`Node ${startNodeId} does not exist.`);
      return;
    }

    setIsTraversalRunning(true);
    const visited = new Set();
    const queue = [startNodeId];
    const traversalOrder = [];

    while (queue.length > 0) {
      const current = queue.shift();
      if (!visited.has(current)) {
        traversalOrder.push(current);
        visited.add(current);

        edges
          .filter((edge) => edge.source === current && !visited.has(edge.target))
          .forEach((edge) => queue.push(edge.target));
      }
    }

    animateTraversal(traversalOrder);
  };

  // DFS Traversal with highlighting
  const dfsTraversal = (startNodeId) => {
    if (!nodes.some((node) => node.id === +startNodeId)) {
      alert(`Node ${startNodeId} does not exist.`);
      return;
    }

    setIsTraversalRunning(true);
    const visited = new Set();
    const stack = [startNodeId];
    const traversalOrder = [];

    while (stack.length > 0) {
      const current = stack.pop();
      if (!visited.has(current)) {
        traversalOrder.push(current);
        visited.add(current);

        edges
          .filter((edge) => edge.source === current && !visited.has(edge.target))
          .reverse()
          .forEach((edge) => stack.push(edge.target));
      }
    }

    animateTraversal(traversalOrder);
  };

  // Animate traversal with delay and node highlighting
  const animateTraversal = (traversalOrder) => {
    let index = 0;

    const interval = setInterval(() => {
      if (index < traversalOrder.length) {
        setHighlightedNodes([traversalOrder[index]]);
        setTraversalQueue(traversalOrder.slice(0, index + 1));
        index++;
      } else {
        clearInterval(interval);
        setIsTraversalRunning(false);
        setHighlightedNodes([]);
        setTraversalQueue([]);
      }
    }, 1000);
  };

  // Render graph with D3
  useEffect(() => {
    const svg = d3.select(svgRef.current);
    svg.selectAll("*").remove();

    if (!nodes.length || !edges.length) return;

    const width = 700;
    const height = 400;

    const simulation = d3
      .forceSimulation(nodes)
      .force("link", d3.forceLink(edges).id((d) => d.id).distance(100))
      .force("charge", d3.forceManyBody().strength(-300))
      .force("center", d3.forceCenter(width / 2, height / 2))
      .on("tick", () => {
        svg
          .selectAll(".link")
          .attr("x1", (d) => d.source.x)
          .attr("y1", (d) => d.source.y)
          .attr("x2", (d) => d.target.x)
          .attr("y2", (d) => d.target.y);

        svg
          .selectAll(".node")
          .attr("cx", (d) => d.x)
          .attr("cy", (d) => d.y);
      });

    // Draw edges
    svg
      .append("g")
      .selectAll(".link")
      .data(edges)
      .enter()
      .append("line")
      .attr("class", "link")
      .attr("stroke", "#aaa")
      .attr("stroke-width", 2);

    // Draw nodes
    svg
      .append("g")
      .selectAll(".node")
      .data(nodes)
      .enter()
      .append("circle")
      .attr("class", "node")
      .attr("r", 15)
      .attr("fill", (d) =>
        highlightedNodes.includes(d.id) ? "#ff5722" : "#4CAF50"
      )
      .call(
        d3.drag()
          .on("start", (event, d) => {
            if (!event.active) simulation.alphaTarget(0.3).restart();
            d.fx = event.x;
            d.fy = event.y;
          })
          .on("drag", (event, d) => {
            d.fx = event.x;
            d.fy = event.y;
          })
          .on("end", (event, d) => {
            if (!event.active) simulation.alphaTarget(0);
            d.fx = null;
            d.fy = null;
          })
      );

    // Add node labels
    svg
      .append("g")
      .selectAll(".label")
      .data(nodes)
      .enter()
      .append("text")
      .attr("class", "label")
      .attr("x", (d) => d.x)
      .attr("y", (d) => d.y - 20)
      .text((d) => d.id)
      .attr("text-anchor", "middle")
      .attr("fill", "#000");

    return () => {
      simulation.stop();
    };
  }, [nodes, edges, highlightedNodes]);

  return (
    <div>
      <textarea
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder="Enter edges (e.g., 1 2\n2 3)"
      ></textarea>
      <button onClick={() => validateInput(input)}>Visualize Graph</button>
      <input
        type="text"
        value={bfsStartNode}
        onChange={(e) => setBfsStartNode(e.target.value)}
        placeholder="Enter start node"
      />
      <button onClick={() => bfsTraversal(bfsStartNode)}>BFS</button>
      <button onClick={() => dfsTraversal(bfsStartNode)}>DFS</button>
      <svg ref={svgRef} width={700} height={400}></svg>
      {isTraversalRunning && (
        <div>
          <h3>Traversal Queue: {traversalQueue.join(" → ")}</h3>
        </div>
      )}
    </div>
  );
};

export default GraphVisualization;
