import React, { useEffect, useRef, useState } from "react";
import * as d3 from "d3";
import "./GraphVisualization.css";

const GraphVisualization = () => {
  const [nodes, setNodes] = useState([]); // List of nodes
  const [edges, setEdges] = useState([]); // List of edges
  const [input, setInput] = useState(""); // Input state for adjacency list
  const [error, setError] = useState(""); // Error state for validation
  const [isDirected, setIsDirected] = useState(true); // Toggle between Directed and Undirected

  const svgRef = useRef(); // Reference to the SVG element

  // Handle the input change
  const handleInputChange = (e) => {
    const value = e.target.value;
    setInput(value);
    validateInput(value);
  };

  // Validate and update graph data
  const validateInput = (input) => {
    try {
      const adjList = JSON.parse(input);

      // Validate if the input is an array of arrays
      if (!Array.isArray(adjList) || adjList.some((item) => !Array.isArray(item))) {
        setError("Invalid format! Please enter a valid adjacency list.");
        return;
      }

      // Initialize new nodes and edges
      const newNodes = [];
      const newEdges = [];

      adjList.forEach((neighbors, from) => {
        const fromId = from.toString(); // Ensure node id is a string

        // Add the source node if not already present
        if (!newNodes.some((node) => node.id === fromId)) {
          newNodes.push({ id: fromId });
        }

        neighbors.forEach((to) => {
          const toId = to.toString();

          // Add the target node if not already present
          if (!newNodes.some((node) => node.id === toId)) {
            newNodes.push({ id: toId });
          }

          // Add the edge
          newEdges.push({ source: fromId, target: toId });

          // If the graph is undirected, add the reverse edge
          if (!isDirected) {
            newEdges.push({ source: toId, target: fromId });
          }
        });
      });

      // Remove duplicate edges in undirected mode
      const uniqueEdges = newEdges.filter(
        (edge, index, self) =>
          index === self.findIndex((e) => e.source === edge.source && e.target === edge.target)
      );

      // Update states
      setNodes(newNodes);
      setEdges(uniqueEdges);
      setError("");
    } catch (error) {
      setError("Invalid JSON! Please provide a valid adjacency list.");
    }
  };

  // Draw the graph using D3
  useEffect(() => {
    const svg = d3.select(svgRef.current);
    svg.selectAll("*").remove(); // Clear previous graph

    if (nodes.length === 0 || edges.length === 0) return;

    const width = 600;
    const height = 400;

    // Create a simulation with forces
    const simulation = d3
      .forceSimulation(nodes)
      .force("link", d3.forceLink(edges).id((d) => d.id).distance(100))
      .force("charge", d3.forceManyBody().strength(-300))
      .force("center", d3.forceCenter(width / 2, height / 2))
      .on("tick", ticked);

    // Add links (edges)
    const link = svg
      .append("g")
      .attr("class", "links")
      .selectAll("line")
      .data(edges)
      .enter()
      .append("line")
      .attr("stroke", "#333")
      .attr("stroke-width", 2)
      .attr("marker-end", isDirected ? "url(#arrowhead)" : null);

    // Add nodes
    const node = svg
      .append("g")
      .attr("class", "nodes")
      .selectAll("circle")
      .data(nodes)
      .enter()
      .append("circle")
      .attr("r", 20)
      .attr("fill", "#4CAF50")
      .attr("stroke", "black")
      .attr("stroke-width", 2)
      .call(
        d3
          .drag()
          .on("start", dragStarted)
          .on("drag", dragged)
          .on("end", dragEnded)
      );

    // Add labels
    const label = svg
      .append("g")
      .attr("class", "labels")
      .selectAll("text")
      .data(nodes)
      .enter()
      .append("text")
      .text((d) => d.id)
      .attr("font-size", "12px")
      .attr("text-anchor", "middle")
      .attr("dy", ".35em")
      .attr("fill", "white");

    // Tick handler
    function ticked() {
      link
        .attr("x1", (d) => d.source.x)
        .attr("y1", (d) => d.source.y)
        .attr("x2", (d) => d.target.x)
        .attr("y2", (d) => d.target.y);

      node.attr("cx", (d) => d.x).attr("cy", (d) => d.y);

      label.attr("x", (d) => d.x).attr("y", (d) => d.y);
    }

    // Drag event handlers
    function dragStarted(event, d) {
      if (!event.active) simulation.alphaTarget(0.3).restart();
      d.fx = d.x;
      d.fy = d.y;
    }

    function dragged(event, d) {
      d.fx = event.x;
      d.fy = event.y;
    }

    function dragEnded(event, d) {
      if (!event.active) simulation.alphaTarget(0);
      d.fx = null;
      d.fy = null;
    }

    return () => simulation.stop();
  }, [nodes, edges, isDirected]);

  return (
    <div className="graph-visualization-container">
      <h1>{isDirected ? "Directed" : "Undirected"} Graph Visualization</h1>

      {/* Buttons for toggling between directed and undirected */}
      <div className="button-container">
        <button
          className={`toggle-button ${isDirected ? "active" : ""}`}
          onClick={() => {
            setIsDirected(true);
            validateInput(input); // Re-validate input to update graph
          }}
        >
          Directed
        </button>
        <button
          className={`toggle-button ${!isDirected ? "active" : ""}`}
          onClick={() => {
            setIsDirected(false);
            validateInput(input); // Re-validate input to update graph
          }}
        >
          Undirected
        </button>
      </div>

      <input
        type="text"
        placeholder='Enter adjacency list as JSON (e.g., [[1,2],[3,4]])'
        onChange={handleInputChange}
        className="input-container"
        value={input}
      />
      {error && <div className="error-message">{error}</div>}

      <div className="graph-display">
        <svg ref={svgRef} width="600" height="400" style={{ border: "1px solid black" }}>
          {/* Define arrowhead marker */}
          <defs>
            <marker
              id="arrowhead"
              markerWidth="10"
              markerHeight="7"
              refX="10"
              refY="3.5"
              orient="auto"
              markerUnits="strokeWidth"
            >
              <polygon points="0 0, 10 3.5, 0 7" fill="#333" />
            </marker>
          </defs>
        </svg>
      </div>
    </div>
  );
};

export default GraphVisualization;
