const step = (currentArr, currentI) => {
  if (isPaused || currentI >= currentArr.length) {
    if (currentI >= currentArr.length) {
      setStatus("Sorting complete!");
    }
    return;
  }

  const key = currentArr[currentI];
  setKey(key);
  setJ(currentI - 1);
  setStatus(`Sorting element ${key}...`);
  movePointer("i-pointer", currentI, "↑");

  // Highlight the current element
  updateArray(currentArr, currentI, key, true); // Apply highlight
  innerStep([...currentArr], key, currentI, currentI - 1);
};

const innerStep = (currentArr, key, currentI, currentJ) => {
  if (isPaused) return;

  if (currentJ >= 0 && currentArr[currentJ] > key) {
    currentArr[currentJ + 1] = currentArr[currentJ];
    updateArray(currentArr, currentJ + 1, currentArr[currentJ], true);
    setJ(currentJ - 1);
    movePointer("j-pointer", currentJ, "↑");

    let timeoutId = setTimeout(() => innerStep([...currentArr], key, currentI, currentJ - 1), ANIMATION_DELAY);
    timeouts.current.push(timeoutId);
  } else {
    currentArr[currentJ + 1] = key;
    updateArray(currentArr, currentJ + 1, key, false); // Remove highlight and mark sorted

    setStatus(`Inserted ${key} at position ${currentJ + 1}`);
    setI(currentI + 1);
    setJ(null);
    movePointer("j-pointer", -1, "↑"); // Hide j-pointer

    let timeoutId = setTimeout(() => step([...currentArr], currentI + 1), ANIMATION_DELAY);
    timeouts.current.push(timeoutId);
  }
};

const updateArray = (newArr, index, value, highlight) => {
  setArray([...newArr]);

  const box = document.getElementById(`box-${index}`);
  if (box) {
    box.innerText = value;
    if (highlight) {
      box.classList.add("highlight"); // Add highlight class
    } else {
      box.classList.remove("highlight"); // Remove highlight
      box.classList.add("sorted"); // Add sorted class
    }
  }
};
