import React, { useState } from "react";
import "./GraphVisualization.css";

const GraphVisualization = () => {
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  const [positions, setPositions] = useState({}); // Store node positions

  const handleInputChange = (e) => {
    const input = e.target.value;
    try {
      const adjList = JSON.parse(input);

      if (typeof adjList !== "object" || Array.isArray(adjList)) {
        alert("Invalid format! Please enter a valid adjacency list as a JSON object.");
        return;
      }

      // Parse nodes and edges
      const parsedNodes = Object.keys(adjList).map((id) => ({ id }));
      const parsedEdges = [];

      Object.entries(adjList).forEach(([from, neighbors]) => {
        neighbors.forEach((to) => {
          parsedEdges.push({ from, to });
        });
      });

      // Generate random positions for each node
      const newPositions = {};
      parsedNodes.forEach((node) => {
        newPositions[node.id] = {
          x: Math.random() * 500 + 50,
          y: Math.random() * 300 + 50,
        };
      });

      setNodes(parsedNodes);
      setEdges(parsedEdges);
      setPositions(newPositions);
    } catch (error) {
      alert("Invalid input! Please provide a valid JSON object.");
    }
  };

  return (
    <div className="graph-visualization-container">
      <h1>Graph Visualization</h1>
      <input
        type="text"
        placeholder='Enter adjacency list as JSON (e.g., {"A": ["B", "C"], "B": [], "C": ["B"]})'
        onChange={handleInputChange}
        className="input-container"
      />
      <div className="graph-display">
        <svg width="600" height="400" style={{ border: "1px solid black" }}>
          {/* Render edges */}
          {edges.map((edge, index) => {
            const fromPos = positions[edge.from];
            const toPos = positions[edge.to];

            if (!fromPos || !toPos) return null;

            return (
              <line
                key={index}
                x1={fromPos.x}
                y1={fromPos.y}
                x2={toPos.x}
                y2={toPos.y}
                stroke="black"
                strokeWidth={2}
              />
            );
          })}

          {/* Render nodes */}
          {nodes.map((node) => {
            const pos = positions[node.id];
            if (!pos) return null;

            return (
              <circle
                key={node.id}
                cx={pos.x}
                cy={pos.y}
                r={20}
                fill="skyblue"
                stroke="black"
                strokeWidth={2}
              >
                <title>{node.id}</title>
              </circle>
            );
          })}
        </svg>
      </div>
    </div>
  );
};

export default GraphVisualization;
