import React, { useEffect, useRef, useState } from "react";
import * as d3 from "d3";
import { toPng } from "html-to-image"; // Library for converting HTML to image
import "./GraphVisualization.css";
import db from "./assets/down-arrow_752650.png";

const GraphVisualization = () => {
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  const [input, setInput] = useState("");
  const [error, setError] = useState("");
  const [isDirected, setIsDirected] = useState(true);
  const [bfsStartNode, setBfsStartNode] = useState("");

  const svgRef = useRef();
  const graphContainerRef = useRef(); // Ref for the graph container

  const handleInputChange = (e) => {
    const value = e.target.value;
    setInput(value);
    validateInput(value);
  };

  const validateInput = (input) => {
    try {
      const lines = input.trim().split("\n");
      const newNodes = [];
      const newEdges = [];

      lines.forEach((line) => {
        const parts = line.trim().split(" ");
        if (parts.length < 2 || isNaN(parts[0]) || isNaN(parts[1])) {
          throw new Error(
            "Invalid input format! Each line must have two integers, and optionally a weight."
          );
        }

        const [from, to, weight] = parts;
        if (!newNodes.some((node) => node.id === from)) newNodes.push({ id: from });
        if (!newNodes.some((node) => node.id === to)) newNodes.push({ id: to });

        const edgeWeight = weight ? parseInt(weight) : null;
        newEdges.push({ source: from, target: to, weight: edgeWeight });

        if (!isDirected) {
          newEdges.push({ source: to, target: from, weight: edgeWeight });
        }
      });

      const uniqueEdges = newEdges.filter(
        (edge, index, self) =>
          index ===
          self.findIndex(
            (e) =>
              (e.source === edge.source && e.target === edge.target) ||
              (e.source === edge.target && e.target === edge.source)
          )
      );

      setNodes(newNodes);
      setEdges(uniqueEdges);
      setError("");
    } catch (error) {
      setError(error.message);
    }
  };

  const downloadGraph = () => {
    if (graphContainerRef.current) {
      toPng(graphContainerRef.current)
        .then((dataUrl) => {
          const link = document.createElement("a");
          link.download = "graph_visualization.png";
          link.href = dataUrl;
          link.click();
        })
        .catch((err) => {
          console.error("Failed to download graph as PNG:", err);
        });
    }
  };

  useEffect(() => {
    const svg = d3.select(svgRef.current);
    svg.selectAll("*").remove();

    if (nodes.length === 0 || edges.length === 0) return;

    const width = 700;
    const height = 350;

    const simulation = d3
      .forceSimulation(nodes)
      .force("link", d3.forceLink(edges).id((d) => d.id).distance(140))
      .force("charge", d3.forceManyBody().strength(-300))
      .force("center", d3.forceCenter(width / 2, height / 2))
      .force("x", d3.forceX(width / 2).strength(0.1))
      .force("y", d3.forceY(height / 2).strength(0.1))
      .on("tick", ticked);

    function constrainBounds(d) {
      d.x = Math.max(20, Math.min(width - 20, d.x));
      d.y = Math.max(20, Math.min(height - 20, d.y));
    }

    svg
      .append("defs")
      .append("marker")
      .attr("id", "arrowhead")
      .attr("viewBox", "0 -5 10 10")
      .attr("refX", 25)
      .attr("refY", 0)
      .attr("orient", "auto")
      .attr("markerWidth", 6)
      .attr("markerHeight", 6)
      .append("path")
      .attr("d", "M0,-5L10,0L0,5")
      .attr("fill", "#42a5f5");

    const link = svg
      .append("g")
      .attr("class", "links")
      .selectAll("line")
      .data(edges)
      .enter()
      .append("line")
      .attr("stroke", "#333")
      .attr("stroke-width", 2)
      .attr("marker-end", isDirected ? "url(#arrowhead)" : null);

    const node = svg
      .append("g")
      .attr("class", "nodes")
      .selectAll("circle")
      .data(nodes)
      .enter()
      .append("circle")
      .attr("r", 20)
      .attr("fill", "#4CAF50")
      .attr("stroke", "black")
      .attr("stroke-width", 2)
      .call(
        d3
          .drag()
          .on("start", dragStarted)
          .on("drag", dragged)
          .on("end", dragEnded)
      );

    const label = svg
      .append("g")
      .attr("class", "labels")
      .selectAll("text")
      .data(nodes)
      .enter()
      .append("text")
      .text((d) => d.id)
      .attr("font-size", "12px")
      .attr("text-anchor", "middle")
      .attr("dy", ".35em")
      .attr("fill", "white");

    const edgeLabels = svg
      .append("g")
      .attr("class", "edge-labels")
      .selectAll("text")
      .data(edges)
      .enter()
      .append("text")
      .text((d) => (d.weight !== null ? d.weight : ""))
      .attr("font-size", "12px")
      .attr("text-anchor", "middle")
      .attr("dy", "-10px")
      .attr("fill", "black");

    function ticked() {
      link
        .attr("x1", (d) => d.source.x)
        .attr("y1", (d) => d.source.y)
        .attr("x2", (d) => d.target.x)
        .attr("y2", (d) => d.target.y);

      node.each(constrainBounds).attr("cx", (d) => d.x).attr("cy", (d) => d.y);

      label.attr("x", (d) => d.x).attr("y", (d) => d.y);
      edgeLabels
        .attr("x", (d) => (d.source.x + d.target.x) / 2)
        .attr("y", (d) => (d.source.y + d.target.y) / 2);
    }

    function dragStarted(event, d) {
      if (!event.active) simulation.alphaTarget(0.3).restart();
      d.fx = d.x;
      d.fy = d.y;
    }

    function dragged(event, d) {
      d.fx = event.x;
      d.fy = event.y;
    }

    function dragEnded(event, d) {
      if (!event.active) simulation.alphaTarget(0);
      d.fx = null;
      d.fy = null;
    }

    return () => simulation.stop();
  }, [nodes, edges, isDirected]);

  const bfsTraversal = (startNode) => {
    if (!nodes.some((node) => node.id === startNode)) {
      alert(`Node ${startNode} does not exist in the graph.`);
      return;
    }
  
    const visited = new Set();
    const queue = [];
    const traversalOrder = [];
  
    queue.push(startNode);
    visited.add(startNode);
  
    const bfsStep = () => {
      if (queue.length === 0) {
        console.log("BFS Traversal Complete:", traversalOrder);
        return;
      }
  
      const currentNode = queue.shift();
      traversalOrder.push(currentNode);
  
      // Highlight the current node
      d3.select(svgRef.current)
        .selectAll("circle")
        .filter((d) => d.id === currentNode)
        .transition()
        .duration(500)
        .attr("fill", "#ff5722");
  
      edges.forEach((edge) => {
        if (edge.source.id === currentNode && !visited.has(edge.target.id)) {
          queue.push(edge.target.id);
          visited.add(edge.target.id);
        } else if (edge.target.id === currentNode && !visited.has(edge.source.id)) {
          queue.push(edge.source.id);
          visited.add(edge.source.id);
        }
      });
  
      setTimeout(bfsStep, 1000); // Recursive call for BFS steps
    };
  
    bfsStep(); // Start the traversal
  };
  
  const handleBFS = (startNode) => {
    if (!startNode) {
      alert("Please enter a start node for BFS.");
      return;
    }
    bfsTraversal(startNode);
  };
  
  return (
    <div className="graph-visualization-container">
      <h1>{isDirected ? "Directed" : "Undirected"} Graph Visualization</h1>

      <div className="button-container">
        <button
          className={`toggle-button ${isDirected ? "active" : ""}`}
          onClick={() => {
            setIsDirected(true);
            validateInput(input);
          }}
        >
          Directed
        </button>
        <button
          className={`toggle-button ${!isDirected ? "active" : ""}`}
          onClick={() => {
            setIsDirected(false);
            validateInput(input);
          }}
        >
          Undirected
        </button>
      </div>

      <div className="input-download-container">
        <textarea
          placeholder={`Enter edges (e.g., "1 2 3" for edge with weight 3) one per line`}
          onChange={handleInputChange}
          className="input-container"
          value={input}
          rows="5"
        ></textarea>
        <div className="download-container">
          <button className="download-button" onClick={downloadGraph}>
            <img src={db} alt="Download" className="download-icon" />
          </button>
          <p className="download-txt">Download Graph</p>
        </div>
      </div>
      {error && <div className="error-message">{error}</div>}

      <div className="operation-group">
  <input
    type="text"
    placeholder="Start node"
    value={bfsStartNode}
    onChange={(e) => setBfsStartNode(e.target.value)}
    className="start-node-input"
  />
  <button className="operation_button" onClick={() => handleBFS(bfsStartNode)}>
    Start BFS
  </button>
</div>
      <div ref={graphContainerRef}>
        <svg ref={svgRef} width="700" height="350"></svg>
      </div>
    </div>
  );
};

export default GraphVisualization;
