import React, { useState, useRef, useEffect } from "react";
import "./InsertionSort.css";

const InsertionSort = () => {
  const [array, setArray] = useState([]);
  const [key, setKey] = useState(null);
  const [i, setI] = useState(1);
  const [j, setJ] = useState(null);
  const [isPaused, setIsPaused] = useState(false);
  const [status, setStatus] = useState("");
  const timeouts = useRef([]);
  const ANIMATION_DELAY = 1500; // Delay between steps in milliseconds
  const pausedState = useRef({}); // Store the paused state

  useEffect(() => {
    return () => {
      timeouts.current.forEach((timeout) => clearTimeout(timeout));
    };
  }, []);

  const resetSortingState = () => {
    setIsPaused(false);
    timeouts.current.forEach((timeout) => clearTimeout(timeout));
    timeouts.current = [];
    setArray([]);
    setKey(null);
    setI(1);
    setJ(null);
    setStatus("");
  };

  const togglePause = () => {
    setIsPaused((prev) => {
      if (prev) {
        // Resume from the paused state
        resumeSorting();
      } else {
        // Pause the sorting by clearing the timeouts and saving the current state
        pausedState.current = { array, i, j, key };
        timeouts.current.forEach((timeout) => clearTimeout(timeout));
        timeouts.current = [];
      }
      return !prev;
    });
  };

  const resumeSorting = () => {
    const { array, i, j, key } = pausedState.current;
    setArray([...array]);
    setI(i);
    setJ(j);
    setKey(key);

    if (j !== null) {
      innerStep([...array], key, i, j);
    } else {
      step([...array], i);
    }
  };

  const startSorting = (inputArray) => {
    resetSortingState();
    const parsedArray = inputArray.split(" ").map(Number);

    if (parsedArray.some(isNaN)) {
      alert("Please enter valid numbers separated by spaces.");
      return;
    }

    setArray(parsedArray);
    setStatus("Starting sort...");
    setTimeout(() => step(parsedArray, 1), 500);
  };

  const step = (currentArr, currentI) => {
    if (isPaused || currentI >= currentArr.length) {
      if (currentI >= currentArr.length) {
        setStatus("Sorting complete!");
      }
      return;
    }

    const key = currentArr[currentI];
    setKey(key);
    setJ(currentI - 1);
    setStatus(`Sorting element ${key}...`);
    movePointer("i-pointer", currentI);

    innerStep([...currentArr], key, currentI, currentI - 1);
  };

  const innerStep = (currentArr, key, currentI, currentJ) => {
    if (isPaused) return;

    if (currentJ >= 0 && currentArr[currentJ] > key) {
      currentArr[currentJ + 1] = currentArr[currentJ];
      updateArray(currentArr, currentJ + 1, currentArr[currentJ]);
      setJ(currentJ - 1);
      movePointer("j-pointer", currentJ);

      let timeoutId = setTimeout(() => innerStep([...currentArr], key, currentI, currentJ - 1), ANIMATION_DELAY);
      timeouts.current.push(timeoutId);
    } else {
      currentArr[currentJ + 1] = key;
      updateArray(currentArr, currentJ + 1, key);

      setStatus(`Inserted ${key} at position ${currentJ + 1}`);
      setI(currentI + 1);
      setJ(null);
      movePointer("j-pointer", -1); // Hide j-pointer

      let timeoutId = setTimeout(() => step([...currentArr], currentI + 1), ANIMATION_DELAY);
      timeouts.current.push(timeoutId);
    }
  };

  const updateArray = (newArr, index, value) => {
    setArray([...newArr]);
    const box = document.getElementById(`box-${index}`);
    if (box) box.innerText = value;
  };

  const movePointer = (pointerId, index) => {
    const pointer = document.getElementById(pointerId);
    if (pointer) {
      if (index === -1) {
        pointer.style.display = "none"; // Hide pointer
      } else {
        pointer.style.display = "block"; // Show pointer
        pointer.innerText = `${pointerId[0]}: ${index}`; // Label pointer with index
        const box = document.getElementById(`box-${index}`);
        if (box) {
          pointer.style.left = box.offsetLeft + box.offsetWidth / 2 - 10 + "px";
          pointer.style.top = box.offsetTop + box.offsetHeight + 10 + "px"; // Below the tile
        }
      }
    }
  };

  return (
    <div className="insertion-sort-container">
      <div className="controls">
        <input
          id="arrayInput"
          type="text"
          placeholder="Enter numbers separated by space"
        />
        <button id="sortButton" onClick={() => startSorting(document.getElementById("arrayInput").value)}>
          Start Sorting
        </button>
        <button id="pauseButton" onClick={togglePause}>
          {isPaused ? "Resume Animation" : "Pause Animation"}
        </button>
      </div>

      <div id="visualization" style={{ display: "flex", gap: "10px", position: "relative" }}>
        {array.map((num, idx) => (
          <div key={idx} id={`box-${idx}`} className="box">
            {num}
          </div>
        ))}
        <div id="i-pointer" className="pointer">i "=" </div>
        <div id="j-pointer" className="pointer">j"</div>
      </div>

      

      <div id="status">{status}</div>
    </div>
  );
};

export default InsertionSort;
