import React, { useState } from "react";
import "./TreeVisualisation.css";

const TreeVisualizer = () => {
  const [input, setInput] = useState('[1,2,3,4,5,null,8,null,null,6,7,9]');
  const [treeData, setTreeData] = useState(null);

  // Function to build the tree from a level-order array
  const buildTree = (arr) => {
    if (!arr.length) return null;

    const nodes = arr.map((value) => (value !== null ? { value, left: null, right: null } : null));

    for (let i = 0, j = 1; j < arr.length; i++) {
      if (nodes[i] !== null) {
        if (j < arr.length) nodes[i].left = nodes[j++];
        if (j < arr.length) nodes[i].right = nodes[j++];
      }
    }

    return nodes[0];
  };

  // Handle "Visualize Tree" button click
  const handleVisualize = () => {
    try {
      const parsedArray = JSON.parse(input);
      setTreeData(buildTree(parsedArray));
    } catch (error) {
      alert("Invalid input format. Please enter a valid JSON array.");
    }
  };

  // Recursive function to render the tree nodes and lines
  const renderTree = (node, level = 0, startX = 50, startY = 50, parentX = null, parentY = null) => {
    if (!node) return null;

    const horizontalSpacing = 50; // Fixed distance between sibling nodes
    const verticalSpacing = 100;  // Vertical spacing between levels

    // Calculate the position for the current node
    const totalNodesAtLevel = Math.pow(2, level);  // Total number of nodes at the current level
    const offset = totalNodesAtLevel > 1 ? horizontalSpacing * (totalNodesAtLevel / 2) : 0;
    const nodePositionX = startX - offset + horizontalSpacing * (level % 2 === 0 ? 1 : -1);

    // Set the X position for the next level's nodes
    const nextLevelStartX = startX + totalNodesAtLevel * horizontalSpacing;

    // Vertical positioning for the current node
    const nextLevelStartY = startY + verticalSpacing;

    return (
      <>
        {/* Line connecting parent node to child */}
        {parentX !== null && (
          <line
            x1={parentX}
            y1={parentY + 20}
            x2={nodePositionX}
            y2={startY - 20}
            stroke="black"
            strokeWidth="2"
          />
        )}

        {/* Circle representing the current node */}
        <g className="circle-group">
          <circle cx={nodePositionX} cy={startY} r="20" className="tree-circle" />
          <text x={nodePositionX} y={startY} textAnchor="middle" dy="5" fill="white" fontWeight="bold">
            {node.value}
          </text>
        </g>

        {/* Recursively render left and right children */}
        {node.left && renderTree(node.left, level + 1, startX, nextLevelStartY, nodePositionX, startY)}
        {node.right && renderTree(node.right, level + 1, nextLevelStartX, nextLevelStartY, nodePositionX, startY)}
      </>
    );
  };

  // Handle "Download Tree" button click
  const handleDownload = () => {
    const svgElement = document.getElementById("treeCanvas");
    const svgData = new XMLSerializer().serializeToString(svgElement);
    const blob = new Blob([svgData], { type: "image/svg+xml" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "tree.svg";
    a.click();
  };

  return (
    <div className="tree-visualizer-container">
      <h1 className="title">Tree Visualizer</h1>

      {/* Textarea for input */}
      <textarea
        className="input-box"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder="Enter a level-order array, e.g., [1,2,3,4,5,null,8,null,null,6,7,9]"
      />

      {/* Buttons for actions */}
      <div className="button-container">
        <button className="visualize-button" onClick={handleVisualize}>
          Visualize Tree
        </button>
        <button className="download-button" onClick={handleDownload}>
          Download Tree
        </button>
      </div>

      {/* Canvas for rendering the tree */}
      <div className="canvas-container">
        <svg id="treeCanvas" className="tree-canvas" viewBox="0 0 1000 600">
          {treeData && renderTree(treeData, 500, 50)} {/* Root starts at center */}
        </svg>
      </div>
    </div>
  );
};

export default TreeVisualizer;
