import React, { useEffect, useRef, useState } from "react";
import * as d3 from "d3";
import { toPng } from "html-to-image";
import "./GraphVisualization.css";
import db from "./assets/down-arrow_752650.png";

const GraphVisualization = () => {
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  const [input, setInput] = useState("");
  const [error, setError] = useState("");
  const [isDirected, setIsDirected] = useState(true);
  const [shortestPathResult, setShortestPathResult] = useState("");
  const [shortestPathInput, setShortestPathInput] = useState({
    startNode: "",
    endNode: "",
  });

  const svgRef = useRef();
  const graphContainerRef = useRef();

  const handleInputChange = (e) => {
    const value = e.target.value;
    setInput(value);
    validateInput(value);
  };

  const validateInput = (input) => {
    try {
      const lines = input.trim().split("\n");
      const newNodes = [];
      const newEdges = [];

      lines.forEach((line) => {
        const parts = line.trim().split(" ");
        if (parts.length < 2 || isNaN(parts[0]) || isNaN(parts[1])) {
          throw new Error(
            "Invalid input format! Each line must have two integers, and optionally a weight."
          );
        }

        const [from, to, weight] = parts;
        if (!newNodes.some((node) => node.id === from))
          newNodes.push({ id: from });
        if (!newNodes.some((node) => node.id === to))
          newNodes.push({ id: to });

        const edgeWeight = weight ? parseInt(weight) : 1; // Default weight is 1 if not provided
        newEdges.push({ source: from, target: to, weight: edgeWeight });

        if (!isDirected) {
          newEdges.push({ source: to, target: from, weight: edgeWeight });
        }
      });

      setNodes(newNodes);
      setEdges(newEdges);
      setError("");
    } catch (error) {
      setError(error.message);
    }
  };

  const findShortestPath = () => {
    const { startNode, endNode } = shortestPathInput;

    if (!startNode || !endNode) {
      setError("Please provide both start and end nodes.");
      return;
    }

    const graph = new Map();

    // Build adjacency list with weights
    edges.forEach(({ source, target, weight }) => {
      if (!graph.has(source)) graph.set(source, []);
      graph.get(source).push({ node: target, weight });
      if (!isDirected) {
        if (!graph.has(target)) graph.set(target, []);
        graph.get(target).push({ node: source, weight });
      }
    });

    // Dijkstra's Algorithm
    const distances = {};
    const previousNodes = {};
    const pq = new Set(nodes.map((node) => node.id));

    nodes.forEach(({ id }) => {
      distances[id] = id === startNode ? 0 : Infinity;
      previousNodes[id] = null;
    });

    while (pq.size) {
      const current = [...pq].reduce((a, b) =>
        distances[a] < distances[b] ? a : b
      );

      pq.delete(current);

      if (current === endNode) break;

      const neighbors = graph.get(current) || [];
      neighbors.forEach(({ node: neighbor, weight }) => {
        const alt = distances[current] + weight;
        if (alt < distances[neighbor]) {
          distances[neighbor] = alt;
          previousNodes[neighbor] = current;
        }
      });
    }

    // Reconstruct the shortest path
    let path = [];
    let currentNode = endNode;
    while (currentNode) {
      path.unshift(currentNode);
      currentNode = previousNodes[currentNode];
    }

    if (path[0] !== startNode) {
      setError(`No path exists between ${startNode} and ${endNode}.`);
      setShortestPathResult("");
    } else {
      setError("");
      setShortestPathResult(path.join(" → "));
    }
  };

  return (
    <div className="graph-visualization-container">
      <h1>{isDirected ? "Directed" : "Undirected"} Graph Visualization</h1>

      <div className="button-container">
        <button
          className={`toggle-button ${isDirected ? "active" : ""}`}
          onClick={() => {
            setIsDirected(true);
            validateInput(input);
          }}
        >
          Directed
        </button>
        <button
          className={`toggle-button ${!isDirected ? "active" : ""}`}
          onClick={() => {
            setIsDirected(false);
            validateInput(input);
          }}
        >
          Undirected
        </button>
      </div>

      <div className="input-download-container">
        <textarea
          placeholder={`Enter edges (e.g., "1 2 3" for edge with weight 3) one per line`}
          onChange={handleInputChange}
          className="input-container"
          value={input}
          rows="5"
        ></textarea>
      </div>

      <div className="graphdislpay-button-wraper">
        <div className="operation-button-container">
          <button className="operation_button" onClick={findShortestPath}>
            Shortest Path
          </button>
          <input
            type="text"
            placeholder="Start Node"
            onChange={(e) =>
              setShortestPathInput({ ...shortestPathInput, startNode: e.target.value })
            }
            style={{ marginLeft: "10px" }}
          />
          <input
            type="text"
            placeholder="End Node"
            onChange={(e) =>
              setShortestPathInput({ ...shortestPathInput, endNode: e.target.value })
            }
            style={{ marginLeft: "10px" }}
          />
        </div>
      </div>

      {shortestPathResult && (
        <div className="operation-result">
          <h3>Shortest Path:</h3>
          <p>{shortestPathResult}</p>
        </div>
      )}

      {error && <div className="error-message">{error}</div>}
    </div>
  );
};

export default GraphVisualization;
